CREATE OR REPLACE PROCEDURE gerar_diagnostico_medico (nr_atendimento_p bigint, dt_diagnostico_p timestamp, cd_medico_diag_p text, nm_usuario_p text) AS $body$
DECLARE


ie_gerar_diag_w	varchar(1);


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (dt_diagnostico_p IS NOT NULL AND dt_diagnostico_p::text <> '') and (cd_medico_diag_p IS NOT NULL AND cd_medico_diag_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	/* consistir diag x atend x data */

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_gerar_diag_w
	from	diagnostico_medico
	where	nr_atendimento	= nr_atendimento_p
	and	dt_diagnostico	= dt_diagnostico_p;

	if (ie_gerar_diag_w = 'S') then
		insert into diagnostico_medico(
							nr_atendimento,
							dt_diagnostico,
							ie_tipo_diagnostico,
							cd_medico,
							dt_atualizacao,
							nm_usuario,
							ds_diagnostico,
							ie_tipo_doenca,
							ie_unidade_tempo,
							qt_tempo
							)
		values (
							nr_atendimento_p,
							dt_diagnostico_p,
							2,
							cd_medico_diag_p,
							clock_timestamp(),
							nm_usuario_p,
							null,
							null,
							null,
							null
							);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
