CREATE OR REPLACE PROCEDURE gerar_diagnostico_pend (nr_seq_texto_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_existe_w			bigint;
sequencia_w			bigint;


BEGIN

select	count(*)
into STRICT	ie_existe_w
from	TEXTO_PADRAO_CID
where	nr_seq_texto = nr_seq_texto_p;

if (ie_existe_w > 0) then

	Insert into ATEND_DIAGNOSTICO_PENDENTE(
		NR_ATENDIMENTO,
		DT_REGISTRO, 
		CD_DOENCA_CID, 
		DT_ATUALIZACAO, 
		NM_USUARIO,
		nr_sequencia)
	SELECT	nr_atendimento_p,
		clock_timestamp(),
		cd_doenca_cid,
		clock_timestamp(),
		nm_usuario_p,
		nextval('atend_diagnostico_pendente_seq')
	FROM	TEXTO_PADRAO_CID
	WHERE	nr_seq_texto = nr_seq_texto_p;

	commit;
end if;

END;
$body$
LANGUAGE PLPGSQL
