CREATE OR REPLACE PROCEDURE gerar_diag_atend_de_diag_tumor (nr_seq_diag_tumor_p bigint, nr_atendimento_p bigint, ie_liberar_p text, nm_usuario_p text, nr_seq_atend_cons_pepa_p bigint default null) AS $body$
DECLARE


cd_doenca_w			varchar(10);
ds_diagnostico_w	varchar(255);
dt_diagnostico_w	timestamp;
cd_medico_w			varchar(10);
ie_tipo_diagnostico_w		bigint := null;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then	
     ie_tipo_diagnostico_w := obter_param_usuario(281, 574, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_tipo_diagnostico_w);
	
	
	select	coalesce(ie_tipo_diagnostico_w,ie_tipo_diagnostico),
		cd_doenca_cid,
		ds_observacao,
		cd_medico
	into STRICT	ie_tipo_diagnostico_w,
		cd_doenca_w,
		ds_diagnostico_w,
		cd_medico_w
	from	can_loco_regional
	where	nr_sequencia = nr_seq_diag_tumor_p;

	dt_diagnostico_w := Obter_Dados_Diagnostico(nr_atendimento_p, cd_medico_w, nm_usuario_p, dt_diagnostico_w);


	if (coalesce(ie_tipo_diagnostico_w::text, '') = '') then
		ie_tipo_diagnostico_w	:= 2;
	end if;

	insert into diagnostico_doenca(
		nr_atendimento,
		cd_doenca,
		ie_classificacao_doenca,
		ds_diagnostico,
		dt_atualizacao,
		dt_diagnostico,	
		nm_usuario,
		dt_liberacao,
		ie_tipo_diagnostico,
		ie_situacao,
		nr_seq_loco_reg,
    nr_seq_atend_cons_pepa)
	values (
		nr_atendimento_p,
		cd_doenca_w,
		'P',
		ds_diagnostico_w,
		clock_timestamp(),
		dt_diagnostico_w,
		nm_usuario_p,
		CASE WHEN ie_liberar_p='L' THEN clock_timestamp() END ,
		ie_tipo_diagnostico_w,
		'A',
		nr_seq_diag_tumor_p,
    nr_seq_atend_cons_pepa_p);

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
