CREATE OR REPLACE PROCEDURE gerar_dic_dados_idioma ( cd_funcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_idioma_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	tasy_idioma
order by
	nr_sequencia;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into nr_seq_idioma_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL gerar_funcao_idioma(cd_funcao_p, nr_seq_idioma_w, nm_usuario_p);
		CALL gerar_tabela_sistema_idioma(cd_funcao_p, nr_seq_idioma_w, nm_usuario_p);
		CALL gerar_tabela_atributo_idioma(cd_funcao_p, nr_seq_idioma_w, nm_usuario_p);
		CALL gerar_cor_legenda_idioma(cd_funcao_p, nr_seq_idioma_w, nm_usuario_p);
		CALL gerar_imagem_legenda_idioma(cd_funcao_p, nr_seq_idioma_w, nm_usuario_p);
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
$body$
