CREATE OR REPLACE PROCEDURE gerar_dieta_refeicoes_nutricao ( nr_seq_dieta_p bigint, ds_dietas_p text, ds_refeicoes_p text, nm_usuario_p text) AS $body$
DECLARE


ds_refeicoes_w		varchar(4000);
ds_codigo_temp_w	varchar(4000);
ds_codigo_aux_w		varchar(2);
nr_seq_dieta_w		bigint;

cd_pessoa_fisica_w	varchar(10);
dt_dieta_w		timestamp;
cd_setor_atendimento_w	integer;

k			smallint;
i			smallint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from 	mapa_dieta
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	cd_setor_atendimento = cd_setor_atendimento_w
	and	cd_refeicao = ds_codigo_temp_w
	and	dt_dieta = dt_dieta_w
	and	coalesce(nr_seq_superior::text, '') = ''
	and	ie_destino_dieta = 'P';


BEGIN
if (nr_seq_dieta_p IS NOT NULL AND nr_seq_dieta_p::text <> '') then

	select	cd_pessoa_fisica,
		dt_dieta,
		cd_setor_atendimento
	into STRICT	cd_pessoa_fisica_w,
		dt_dieta_w,
		cd_setor_atendimento_w
	from	mapa_dieta
	where	nr_sequencia = nr_seq_dieta_p;

	ds_refeicoes_w := ds_refeicoes_p;
	k := 1;
	for i in k..length(ds_refeicoes_w) loop
		begin

		ds_codigo_aux_w	:= substr(ds_refeicoes_w,i,1);

		if (ds_codigo_aux_w = ',') then

			open C01;
			loop
			fetch C01 into
				nr_seq_dieta_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin
				if (nr_seq_dieta_w IS NOT NULL AND nr_seq_dieta_w::text <> '') then
				   CALL gerar_dieta_nutricao(nr_seq_dieta_w,ds_dietas_p,nm_usuario_p);
				end if;
				end;
			end loop;
			close C01;

			ds_codigo_temp_w := '';
		else
			ds_codigo_temp_w := ds_codigo_temp_w||ds_codigo_aux_w;
		end if;
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
