CREATE OR REPLACE PROCEDURE gerar_dispensacao_keypress (cd_material_p bigint, cd_unid_med_p text, qt_dispensacao_p bigint, nr_cirurgia_p bigint, nm_usuario_p text, ie_operacao_p text, ie_motivo_p text, ds_motivo_p text, nr_seq_lote_fornec_p bigint, dt_validade_p timestamp, ie_origem_gasto_p bigint, nr_seq_agente_p bigint, cd_estabelecimento_p bigint, ie_via_aplicacao_p text, cd_local_estoque_p bigint, ds_just_alergico_p text default null) AS $body$
DECLARE

			
nr_seq_pepo_w	bigint;	
cd_unid_med_w	varchar(30);
ie_opcao_w	   varchar(30);
			

BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (qt_dispensacao_p IS NOT NULL AND qt_dispensacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (ie_operacao_p IS NOT NULL AND ie_operacao_p::text <> '') and (ie_origem_gasto_p IS NOT NULL AND ie_origem_gasto_p::text <> '') then
	begin
	
	if (coalesce(cd_unid_med_p::text, '') = '') then
		begin
		select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMS'),1,30) cd_unidade_medida_consumo
		into STRICT	cd_unid_med_w
		from	material 
		where	cd_material = cd_material_p;
		end;
	else
		begin
		cd_unid_med_w	:= cd_unid_med_p;
		end;
	end if;
	
	CALL grava_cirurgia_dispensacao(	cd_material_p,
					cd_unid_med_w,          
					qt_dispensacao_p,
					nr_cirurgia_p,          
					nm_usuario_p,           
					ie_operacao_p,          
					ie_motivo_p,            
					ds_motivo_p,           
					nr_seq_lote_fornec_p,
					dt_validade_p,
					ie_origem_gasto_p,
					nr_seq_agente_p,
					'N',
               cd_local_estoque_p);
					
	if (ie_origem_gasto_p = 0) then
		ie_opcao_w := 'A';
	elsif (ie_origem_gasto_p = 1) then
		ie_opcao_w := 'C';
	elsif (ie_origem_gasto_p = 2) then	
		ie_opcao_w := 'E';
	elsif (ie_origem_gasto_p = 3) then
		ie_opcao_w := 'P';
	end if;
	
	if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
		begin
		CALL gerar_medicamento_cod_barras(	nr_cirurgia_p,
						cd_material_p,          
						nm_usuario_p,          
						cd_estabelecimento_p,
						nr_seq_agente_p,        
						ie_via_aplicacao_p,     
						nr_seq_pepo_w,          
						ie_opcao_w,
                  ds_just_alergico_p);
		end;
	end if;
	end;
end if;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
