CREATE OR REPLACE PROCEDURE gerar_disp_proc_adep_sol ( dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w		bigint;
nr_prescricao_w		bigint;
nr_seq_solucao_w		integer;
nr_etapa_processo_w	smallint;
nr_seq_processo_w		bigint;

c01 CURSOR FOR 
SELECT	a.nr_atendimento, 
	a.nr_prescricao, 
	a.nr_seq_solucao, 
	1 
from	adep_gestao_solucao_v a 
where	a.dt_status		between	dt_inicial_p and dt_final_p 
and	a.ie_status		= 'N' 
and	coalesce(a.ie_acm,'N')		= 'N' 
and	coalesce(a.ie_se_necessario,'N')	= 'N' 
and	not exists ( 
		SELECT	1 
		from	adep_processo x 
		where	x.nr_prescricao	= a.nr_prescricao 
		and	x.nr_seq_solucao	= a.nr_seq_solucao 
		and	x.nr_etapa	= 1) 
group by 
	a.nr_atendimento, 
	a.nr_prescricao, 
	a.nr_seq_solucao, 
	1 
order by 
	a.nr_atendimento, 
	a.nr_prescricao, 
	a.nr_seq_solucao;


BEGIN 
 
open c01;
loop 
fetch c01 into	nr_atendimento_w, 
		nr_prescricao_w, 
		nr_seq_solucao_w, 
		nr_etapa_processo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	nr_seq_processo_w := gerar_processo_adep_sol(nr_atendimento_w, nr_prescricao_w, nr_seq_solucao_w, nr_etapa_processo_w, null, nm_usuario_p, nr_seq_processo_w, null, null, 'GDPAS', 'N', null, null);
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
