CREATE OR REPLACE PROCEDURE gerar_distribuicao_receita ( nr_seq_regra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_mens_w		w_distribuicao_rec_lote.nr_seq_lote%type;
nr_seq_lote_rec_w		lote_distribuicao_receita.nr_sequencia%type;
qt_registro_w			bigint;

c_w_lote_mens CURSOR FOR
	SELECT	a.nr_seq_lote
	from	w_distribuicao_rec_lote	a
	where	a.nm_usuario	= nm_usuario_p;


BEGIN
if (nr_seq_regra_p > 0) then
	nr_seq_lote_rec_w	:= null;

	select	count(1)
	into STRICT	qt_registro_w
	from	w_distribuicao_rec_lote	a
	where	a.nm_usuario	= nm_usuario_p  LIMIT 1;

	if (qt_registro_w > 0) then
		select	nextval('lote_distribuicao_receita_seq')
		into STRICT	nr_seq_lote_rec_w
		;

		insert into lote_distribuicao_receita(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			cd_estabelecimento,
			dt_lote,
			nr_seq_regra)
		values (nr_seq_lote_rec_w,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			cd_estabelecimento_p,
			clock_timestamp(),
			nr_seq_regra_p);
	end if;

	open c_w_lote_mens;
	loop
	fetch c_w_lote_mens into
		nr_seq_lote_mens_w;
	EXIT WHEN NOT FOUND; /* apply on c_w_lote_mens */
		begin
		insert into distribuicao_receita_mens(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_lote,
			nr_seq_lote_mens)
		values (nextval('distribuicao_receita_mens_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_lote_rec_w,
			nr_seq_lote_mens_w);
		end;
	end loop;
	close c_w_lote_mens;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
