SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_divergencia_item_nf_oc ( nr_sequencia_p bigint, nr_item_nf_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ie_tipo_regra_w			bigint;
qt_existe_w			bigint;
pr_diferenca_w			real;
qt_diferenca_w			double precision;
qt_diferenca_ww			double precision;
nr_ordem_compra_w		bigint;
cd_material_w			integer;
cd_material_oci_w		integer;
cd_unidade_medida_compra_w	varchar(30);
vl_unitario_material_w		double precision;
qt_material_w			double precision;
pr_desconto_w			double precision;
cd_local_estoque_w		smallint;
nr_item_oci_w			integer;
cd_tipo_divergencia_w		smallint;
nr_seq_divergencia_w		bigint;
cd_estabelecimento_w		smallint;
cd_cgc_emitente_w		varchar(14);
cd_pessoa_fisica_w		varchar(10);
cd_serie_nf_w			nota_fiscal.cd_serie_nf%type;
nr_nota_fiscal_w		varchar(255);
nr_sequencia_nf_w		bigint;
nr_item_nf_w			integer;
qt_item_nf_w			double precision;
vl_unitario_item_nf_w		double precision;
ie_diverg_oc_nf_w		varchar(1);
ds_justificativa_w		varchar(255);
dt_entrega_ordem_w		timestamp;
nr_item_oci_ww			integer;
dt_entrada_saida_w		timestamp;
dt_prevista_entrega_w		timestamp;
qt_item_oci_w			double precision;
vl_unitario_item_oci_w		double precision;
pr_descontos_oci_w		double precision;
cd_local_estoque_oci_w		smallint;
ie_divergencia_w		varchar(1);
vl_desconto_oc_w		double precision;
vl_descontos_nf_w		double precision;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_tipo_nota_w			varchar(3);
nr_seq_entrega_w		bigint;
cd_cond_pagto_ordem_w		bigint;
cd_cond_pagto_nota_w		bigint;
vl_frete_ordem_w		double precision;
vl_despesa_acessoria_oc_w	double precision;
vl_despesa_acessoria_nf_w	double precision;
vl_frete_nota_w			double precision;
ds_material_w			varchar(255);
vl_total_ordem_w		double precision;
vl_total_nota_w			double precision;
vl_ipi_item_oc_w		double precision;
vl_ipi_item_nf_w		double precision;
ie_permite_justif_w		varchar(1);
qt_reg_entrega_w		bigint;
ie_frete_w                	ordem_compra.ie_frete%type;

c01 CURSOR FOR
SELECT	a.cd_estabelecimento,
	a.cd_cgc_emitente,
	n.cd_pessoa_fisica,
	a.nr_nota_fiscal,
	a.cd_serie_nf,
	a.nr_sequencia_nf,
	a.nr_item_nf,
	a.vl_unitario_item_nf,
	a.cd_material,
	a.cd_local_estoque,
	a.nr_ordem_compra,
	a.pr_desconto,
	a.nr_item_oci,
	a.ds_justificativa,
	b.qt_material,
	b.cd_material,
	b.vl_unitario_material,
	b.pr_descontos,
	b.cd_local_estoque,
	a.dt_entrega_ordem,
	b.nr_item_oci,
	coalesce(o.vl_desconto,0),
	coalesce(n.vl_descontos,0),
	o.cd_condicao_pagamento,
	n.cd_condicao_pagamento,
	coalesce(o.vl_despesa_acessoria,0),
	coalesce(n.vl_despesa_acessoria,0),
	obter_vl_ipi_item_oc(o.nr_ordem_compra, b.nr_item_oci) vl_ipi_item_oc,
	obter_vl_ipi_item_nf(n.nr_sequencia, a.nr_item_nf) vl_ipi_item_nf,
	o.vl_frete,	
	n.vl_frete,
	--nvl(obter_valor_liquido_ordem(o.nr_ordem_compra),0),

	/*nvl((   select	sum(nvl(obter_valor_liquido_ordem(y.nr_ordem_compra),0))
		from	ordem_compra y,
			nota_fiscal_item x
		where	x.nr_ordem_compra = y.nr_ordem_compra
		and	x.nr_sequencia = nr_sequencia_p),0),*/
	coalesce((   SELECT	SUM(coalesce(obter_valor_liquido_oci(y.nr_ordem_compra,x.nr_item_oci),0))
		FROM	ordem_compra y,
			nota_fiscal_item x
		WHERE	x.nr_ordem_compra = y.nr_ordem_compra
		AND	x.nr_sequencia = nr_sequencia_p),0),		
	n.vl_total_nota,
	o.ie_frete
from	ordem_compra_item b,
	ordem_compra o,
	nota_fiscal_item a,
	nota_fiscal n
where	a.nr_sequencia		= nr_sequencia_p
and	a.nr_item_nf 		= nr_item_nf_p
and	o.nr_ordem_compra	= b.nr_ordem_compra
and	n.nr_sequencia		= a.nr_sequencia
and	(a.nr_ordem_compra IS NOT NULL AND a.nr_ordem_compra::text <> '')
and	a.nr_ordem_compra	= b.nr_ordem_compra
and	a.nr_item_oci		= b.nr_item_oci;


BEGIN


select	coalesce(max(ie_tipo_nota), 'X')
into STRICT	ie_tipo_nota_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

if (ie_tipo_nota_w in ('EN','EF','EP')) then
	begin
	
	select	b.ie_diverg_oc_nf,
		a.dt_entrada_saida
	into STRICT	ie_diverg_oc_nf_w,
		dt_entrada_saida_w
	from	parametro_compras b,
		nota_fiscal a
	where	a.cd_estabelecimento 	= b.cd_estabelecimento
	and 	a.nr_sequencia		= nr_sequencia_p;

	update	divergencia_ordem_compra_item
	set	ie_situacao = 'I'
	where	nr_seq_nf = nr_sequencia_p;

	open c01;
	loop
	fetch c01 into
		cd_estabelecimento_w,
		cd_cgc_emitente_w,
		cd_pessoa_fisica_w,
	 	nr_nota_fiscal_w,
	 	cd_serie_nf_w,
	 	nr_sequencia_nf_w,
	 	nr_item_nf_w,
	 	vl_unitario_item_nf_w,
	 	cd_material_w,
	 	cd_local_estoque_w,
	 	nr_ordem_compra_w,
	 	pr_desconto_w,
	 	nr_item_oci_w,
		ds_justificativa_w,
	 	qt_item_oci_w,
		cd_material_oci_w,
	 	vl_unitario_item_oci_w,
	 	pr_descontos_oci_w,
	 	cd_local_estoque_oci_w,
		dt_entrega_ordem_w,
		nr_item_oci_ww,
		vl_desconto_oc_w,
		vl_descontos_nf_w,
		cd_cond_pagto_ordem_w,
		cd_cond_pagto_nota_w,
		vl_despesa_acessoria_oc_w,
		vl_despesa_acessoria_nf_w,
		vl_ipi_item_oc_w,
		vl_ipi_item_nf_w,
		vl_frete_ordem_w,
		vl_frete_nota_w,
		vl_total_ordem_w,
		vl_total_nota_w,
		ie_frete_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ie_divergencia_w := 'N';

		begin
		
		select	count(1)
		into STRICT	qt_reg_entrega_w
		from	ordem_compra_item_entrega a
		where	a.nr_ordem_compra 	= nr_ordem_compra_w
		and	a.nr_item_oci 		= nr_item_oci_ww
		and	trunc(a.dt_prevista_entrega,'dd') = trunc(dt_entrega_ordem_w,'dd')
		and	coalesce(a.dt_cancelamento::text, '') = ''
		and	((coalesce(a.dt_real_entrega::text, '') = '') or (coalesce(a.qt_real_entrega,0) < a.qt_prevista_entrega));
			
		if (qt_reg_entrega_w > 0) then
			begin
			
			select	min(a.nr_sequencia)
			into STRICT	nr_seq_entrega_w
			from	ordem_compra_item_entrega a
			where	a.nr_ordem_compra 	= nr_ordem_compra_w
			and	a.nr_item_oci 		= nr_item_oci_ww
			and	trunc(a.dt_prevista_entrega,'dd') = trunc(dt_entrega_ordem_w,'dd')
			and	coalesce(a.dt_cancelamento::text, '') = ''
			and	((coalesce(a.dt_real_entrega::text, '') = '') or (coalesce(a.qt_real_entrega,0) < a.qt_prevista_entrega));
				
			end;
		else	
			begin
			
			select	min(a.nr_sequencia)
			into STRICT	nr_seq_entrega_w
			from	ordem_compra_item_entrega a
			where	a.nr_ordem_compra 	= nr_ordem_compra_w
			and	a.nr_item_oci 		= nr_item_oci_ww
			and	coalesce(a.dt_cancelamento::text, '') = ''
			and	((coalesce(a.dt_real_entrega::text, '') = '') or (coalesce(a.qt_real_entrega,0) < a.qt_prevista_entrega));
				
			end;
		end if;
		
		select	substr(obter_desc_material(cd_material_w),1,255)
		into STRICT	ds_material_w
		;
		
		select	qt_prevista_entrega,
			dt_prevista_entrega
		into STRICT	qt_item_oci_w,
			dt_prevista_entrega_w
		from	ordem_compra_item_entrega
		where	nr_sequencia = nr_seq_entrega_w;
		
		exception when no_data_found then
			/*(-20011,'Nao encontrada data prevista de entrega ou o item ja foi entregue!' || chr(13) || chr(10) ||
							'Item NF: '		|| nr_item_nf_w	|| chr(13) || chr(10) ||
							'Item OCI: '	|| nr_item_oci_ww	|| chr(13) || chr(10) ||
							'Material: '		|| cd_material_w	|| ' - ' || ds_material_w);*/
							
			CALL wheb_mensagem_pck.exibir_mensagem_abort(177760,	'NR_ITEM_NF_W='||nr_item_nf_w||';'||
									'NR_ITEM_OCI_W='||nr_item_oci_ww||';'||
									'CD_MATERIAL_W='||cd_material_w||';'||
									'DS_MATERIAL_W='||ds_material_w);
		end;
		
		select	sum(qt_item_nf)
		into STRICT	qt_item_nf_w
		from	nota_fiscal_item
		
		where	nr_ordem_compra = nr_ordem_compra_w
		and	nr_item_oci = nr_item_oci_w
		and	trunc(dt_entrega_ordem,'dd') = trunc(dt_entrega_ordem_w,'dd');
		
		select  cd_grupo_material,
			cd_subgrupo_material,
			cd_classe_material
		into STRICT	cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w
		from	estrutura_material_v
		where	cd_material = cd_material_w;

		/* 2 - Alteracao na quantidade(a maior)*/

		select	count(*),
			coalesce(max(pr_diferenca),0),
			coalesce(max(qt_diferenca),0),
			coalesce(max(ie_permite_justif),'S')
		into STRICT	qt_existe_w,
			pr_diferenca_w,
			qt_diferenca_w,
			ie_permite_justif_w
		from	regra_diverg_oc_nf
		where	cd_estabelecimento					= cd_estabelecimento_w
		and	coalesce(cd_local_estoque,cd_local_estoque_w)		= cd_local_estoque_w
		and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
		and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
		and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
		and	coalesce(cd_material, cd_material_w)			= cd_material_w
		and	ie_consiste_salvar_item = 'S'
		and	ie_tipo_regra = 2
		and	coalesce(obter_se_restringe_div_oc_nf(nr_sequencia, cd_cgc_emitente_w),'S') = 'S';

		if (pr_diferenca_w > 0) then
			qt_diferenca_w	:= round((dividir((qt_item_oci_w * pr_diferenca_w), 100))::numeric,4);
		end if;
		
		if (qt_existe_w > 0) and (round((qt_diferenca_w + qt_item_oci_w)::numeric,4) < qt_item_nf_w) then		
			begin
			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_divergencia_w
			from	divergencia_ordem_compra_item
			where	cd_tipo_divergencia = 2
			and	ie_situacao	= 'I'
			and	nr_seq_nf	= nr_sequencia_p
			and	nr_item_nf	= nr_item_nf_w
			and	nr_ordem_compra	= nr_ordem_compra_w
			and	nr_item_doi_nfe	= nr_item_oci_w;

			if (nr_seq_divergencia_w > 0) then
				update	divergencia_ordem_compra_item
				set	ie_situacao = 'A'
				where	nr_sequencia = nr_seq_divergencia_w;
			else
				select	coalesce(max(nr_sequencia),0) + 1
				into STRICT	nr_seq_divergencia_w
				from	divergencia_ordem_compra_item;

				insert  into divergencia_ordem_compra_item(
					nr_ordem_compra,
					nr_item_doi_nfe,
					cd_tipo_divergencia,
					cd_estabelecimento,
					cd_cgc_emitente,
					cd_pessoa_fisica,
					cd_serie_nf,
					nr_nota_fiscal,
					nr_sequencia_nf,
					nr_item_nf,
					dt_atualizacao,
					nm_usuario,
					ds_observacao,
					nr_seq_nf,
					nr_sequencia,
					ie_situacao,
					ie_permite_justif,
					dt_prevista_entrega)
				values (	nr_ordem_compra_w,
					nr_item_oci_w,
					2,
					cd_estabelecimento_w,
					cd_cgc_emitente_w,
					cd_pessoa_fisica_w,
					cd_serie_nf_w,
					nr_nota_fiscal_w,
					nr_sequencia_nf_w,
					nr_item_nf_w,
					clock_timestamp(),
					nm_usuario_p,
					null,
					nr_sequencia_p,
					nr_seq_divergencia_w,
					'A',
					ie_permite_justif_w,
					dt_entrega_ordem_w);
			end if;
			ie_divergencia_w := 'S';
			end;
		end if;	
			
		/* 7 - Alteracao na quantidade(a menor) */

		select	count(*),
			coalesce(max(pr_diferenca),0),
			coalesce(max(qt_diferenca),0),
			coalesce(max(ie_permite_justif),'S')
		into STRICT	qt_existe_w,
			pr_diferenca_w,
			qt_diferenca_w,
			ie_permite_justif_w
		from	regra_diverg_oc_nf
		where	cd_estabelecimento					= cd_estabelecimento_w
		and	coalesce(cd_local_estoque,cd_local_estoque_w)		= cd_local_estoque_w
		and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
		and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
		and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
		and	coalesce(cd_material, cd_material_w)				= cd_material_w
		and	ie_consiste_salvar_item = 'S'
		and	ie_tipo_regra = 7
		and	coalesce(obter_se_restringe_div_oc_nf(nr_sequencia, cd_cgc_emitente_w),'S') = 'S';

		if (pr_diferenca_w > 0) then
			qt_diferenca_w	:= round((dividir((qt_item_oci_w * pr_diferenca_w), 100))::numeric,4);
		end if;

		if (qt_existe_w > 0) and (round((abs(qt_diferenca_w - qt_item_oci_w))::numeric,4) > qt_item_nf_w) then
			begin
			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_divergencia_w
			from	divergencia_ordem_compra_item
			where	cd_tipo_divergencia = 7
			and	ie_situacao	= 'I'
			and	nr_seq_nf		= nr_sequencia_p
			and	nr_item_nf	= nr_item_nf_w
			and	nr_ordem_compra	= nr_ordem_compra_w
			and	nr_item_doi_nfe	= nr_item_oci_w;
		
			if (nr_seq_divergencia_w > 0) then
				update	divergencia_ordem_compra_item
				set	ie_situacao = 'A'
				where	nr_sequencia = nr_seq_divergencia_w;
			else
				select	coalesce(max(nr_sequencia),0) + 1
				into STRICT	nr_seq_divergencia_w
				from	divergencia_ordem_compra_item;

				insert  into divergencia_ordem_compra_item(
					nr_ordem_compra,
					nr_item_doi_nfe,
					cd_tipo_divergencia,
					cd_estabelecimento,
					cd_cgc_emitente,
					cd_pessoa_fisica,
					cd_serie_nf,
					nr_nota_fiscal,
					nr_sequencia_nf,
					nr_item_nf,
					dt_atualizacao,
					nm_usuario,
					ds_observacao,
					nr_seq_nf,
					nr_sequencia,
					ie_situacao,
					ie_permite_justif,
					dt_prevista_entrega)
				values (	nr_ordem_compra_w,
					nr_item_oci_w,
					7,
					cd_estabelecimento_w,
					cd_cgc_emitente_w,
					cd_pessoa_fisica_w,
					cd_serie_nf_w,
					nr_nota_fiscal_w,
					nr_sequencia_nf_w,
					nr_item_nf_w,
					clock_timestamp(),
					nm_usuario_p,
					null,
					nr_sequencia_p,
					nr_seq_divergencia_w,
					'A',
					ie_permite_justif_w,
					dt_entrega_ordem_w);
			end if;
			ie_divergencia_w := 'S';
			end;
		end if;
		
		/* 3 - Alteracao preco unitario (a maior) */

		select	count(*),
			coalesce(max(pr_diferenca),0),
			coalesce(max(qt_diferenca),0),
			coalesce(max(ie_permite_justif),'S')
		into STRICT	qt_existe_w,
			pr_diferenca_w,
			qt_diferenca_w,
			ie_permite_justif_w
		from	regra_diverg_oc_nf
		where	cd_estabelecimento					= cd_estabelecimento_w
		and	coalesce(cd_local_estoque,cd_local_estoque_w)		= cd_local_estoque_w
		and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
		and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
		and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
		and	coalesce(cd_material, cd_material_w)			= cd_material_w
		and	ie_consiste_salvar_item = 'S'
		and	ie_tipo_regra = 3
		and	coalesce(obter_se_restringe_div_oc_nf(nr_sequencia, cd_cgc_emitente_w),'S') = 'S';
		
		if (pr_diferenca_w > 0) then
			qt_diferenca_ww	:= round((dividir((vl_unitario_item_oci_w * pr_diferenca_w), 100))::numeric,4);
		end if;
		
		if (qt_existe_w > 0) and
			(((pr_diferenca_w > 0) and (round((qt_diferenca_ww + vl_unitario_item_oci_w)::numeric,4) < vl_unitario_item_nf_w)) or
			((qt_diferenca_w > 0)  and (vl_unitario_item_nf_w > vl_unitario_item_oci_w) and (abs(vl_unitario_item_nf_w - vl_unitario_item_oci_w) > qt_diferenca_w))) then
			begin
			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_divergencia_w
			from	divergencia_ordem_compra_item
			where	cd_tipo_divergencia = 3
			and	ie_situacao	= 'I'
			and	nr_seq_nf		= nr_sequencia_p
			and	nr_item_nf	= nr_item_nf_w
			and	nr_ordem_compra	= nr_ordem_compra_w
			and	nr_item_doi_nfe	= nr_item_oci_w;

			if (nr_seq_divergencia_w > 0) then
				update	divergencia_ordem_compra_item
				set	ie_situacao = 'A'
				where	nr_sequencia = nr_seq_divergencia_w;
			else
				select	coalesce(max(nr_sequencia),0) + 1
				into STRICT	nr_seq_divergencia_w
				from	divergencia_ordem_compra_item;

				insert  into divergencia_ordem_compra_item(
					nr_ordem_compra,
					nr_item_doi_nfe,
					cd_tipo_divergencia,
					cd_estabelecimento,
					cd_cgc_emitente,
					cd_pessoa_fisica,
					cd_serie_nf,
					nr_nota_fiscal,
					nr_sequencia_nf,
					nr_item_nf,
					dt_atualizacao,
					nm_usuario,
					ds_observacao,
					nr_seq_nf,
					nr_sequencia,
					ie_situacao,
					ie_permite_justif,
					dt_prevista_entrega)
				values (	nr_ordem_compra_w,
					nr_item_oci_w,
					3,
