					nr_item_nf,
					dt_atualizacao,
					nm_usuario,
					ds_observacao,
					nr_seq_nf,
					nr_sequencia,
					ie_situacao,
					ie_permite_justif,
					dt_prevista_entrega)
				values (	nr_ordem_compra_w,
					nr_item_oci_w,
					16,
					cd_estabelecimento_w,
					cd_cgc_emitente_w,
					cd_pessoa_fisica_w,
					cd_serie_nf_w,
					nr_nota_fiscal_w,
					nr_sequencia_nf_w,
					nr_item_nf_w,
					clock_timestamp(),
					nm_usuario_p,
					null,
					nr_sequencia_p,
					nr_seq_divergencia_w,
					'A',
					ie_permite_justif_w,
					dt_entrega_ordem_w);
			end if;
			end;
			ie_divergencia_w := 'S';
		end if;
		
		if (ie_divergencia_w = 'S') and
			((ds_justificativa_w = '') or (coalesce(ds_justificativa_w::text, '') = '')) and (ie_diverg_oc_nf_w = 'S') then
			ds_retorno_p := substr(ds_retorno_p || nr_item_nf_w || ' - ',1,255);
		end if;
		end;
		
	end loop;
	close c01;

	delete
	from	divergencia_ordem_compra_item
	where	ie_situacao = 'I'
	and	nr_seq_nf = nr_sequencia_p;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_divergencia_item_nf_oc ( nr_sequencia_p bigint, nr_item_nf_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) FROM PUBLIC;

