CREATE OR REPLACE PROCEDURE gerar_eis_dieta (dt_parametro_P timestamp) AS $body$
DECLARE

 
dt_parametro_w			timestamp;


BEGIN 
/* Insert into Log_xxTasy values (sysdate, 'Tasy', 601, 'Dieta'); 
commit; */
 
dt_parametro_w		:= trunc(dt_parametro_p,'month');
 
delete from eis_Dieta 
where dt_referencia between dt_parametro_w-1 and last_day(dt_parametro_w) + 86399/86400;
 
commit;
insert into eis_dieta( 
	dt_referencia, 
	cd_estabelecimento, 
	cd_refeicao, 
	cd_dieta, 
	ie_destino_dieta, 
	cd_setor_atendimento, 
	ie_sexo, 
	ie_faixa_etaria, 
	qt_dieta, 
	cd_tipo_acomodacao, 
	cd_convenio, 
	nr_seq_mapa) 
SELECT	 
	Trunc(dt_dieta,'dd'), 
	coalesce(OBTER_ESTAB_ATEND(a.nr_atendimento),1), 
	a.cd_refeicao, 
	a.cd_dieta, 
	a.ie_destino_dieta, 
	a.cd_setor_atendimento, 
	coalesce(b.ie_sexo,'I'), 
	substr(obter_idade(b.dt_nascimento, clock_timestamp(), 'E'),1,10), 
	count(*), 
	obter_acomod_atend_data(a.nr_atendimento,a.dt_dieta), 
	obter_convenio_atendimento(a.nr_atendimento), 
	a.nr_sequencia 
from	pessoa_fisica b, 
	Mapa_Dieta a 
where	a.cd_pessoa_fisica 		= b.cd_pessoa_fisica 
 and	a.dt_dieta between dt_parametro_w-1 and fim_dia(last_day(dt_parametro_w)) 
group by 
	Trunc(dt_dieta,'dd'), 
	a.cd_refeicao, 
	a.cd_dieta, 
	a.ie_destino_dieta, 
	a.cd_setor_atendimento, 
	coalesce(b.ie_sexo,'I'), 
	substr(obter_idade(b.dt_nascimento, clock_timestamp(), 'E'),1,10), 
	obter_acomod_atend_data(a.nr_atendimento,a.dt_dieta), 
	obter_convenio_atendimento(a.nr_atendimento), 
	OBTER_ESTAB_ATEND(a.nr_atendimento), 
	a.nr_sequencia;
 
--Insert into Logxxx_Tasy values (sysdate, 'Tasy', 602, 'Dieta'); 
COMMIT;
END;
$body$
