CREATE OR REPLACE PROCEDURE gerar_eis_eficacia_os ( dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_estabelecimento_w	bigint;
cd_setor_atendimento_w	bigint;
qt_ordem_w		bigint;
qt_ordem_eficacia_w	bigint;
dt_inicio_w		timestamp;
dt_final_w		timestamp;

C01 CURSOR FOR
SELECT	b.cd_estabelecimento,
	b.cd_setor,
	count(*) qt_ordem,
	sum(CASE WHEN ie_eficacia='S' THEN 1  ELSE 0 END ) qt_ordem_eficacia
from	man_localizacao b,
	man_ordem_servico a
where 	a.nr_seq_localizacao	= b.nr_sequencia
and	a.dt_fim_real between dt_inicio_w and dt_final_w
and	(ie_eficacia IS NOT NULL AND ie_eficacia::text <> '')
group by
	b.cd_estabelecimento,
	b.cd_setor;


BEGIN

dt_inicio_w		:= trunc(dt_referencia_p,'month');
dt_final_w		:= last_day(dt_inicio_w) + 86399/86400;

delete	from eis_qua_eficacia_os
where	dt_mes_ref	= dt_inicio_w;

OPEN C01;
LOOP
FETCH C01 into
	cd_estabelecimento_w,
	cd_setor_atendimento_w,
	qt_ordem_w,
	qt_ordem_eficacia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	nextval('eis_qua_eficacia_os_seq')
	into STRICT	nr_sequencia_w
	;
	insert	into eis_qua_eficacia_os(
		nr_sequencia,
		cd_estabelecimento,
		dt_mes_ref,
		dt_atualizacao,
		nm_usuario,
		cd_setor_atendimento,
		qt_ordem,
		qt_ordem_eficacia)
	values (
		nr_sequencia_w,
		cd_estabelecimento_w,
		dt_inicio_w,
		clock_timestamp(),
		nm_usuario_p,
		cd_setor_atendimento_w,
		qt_ordem_w,
		qt_ordem_eficacia_w);

END LOOP;
CLOSE C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
