SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_eis_market ( nm_usuario_p text ) AS $body$
DECLARE



SG_ESTADO_w	pessoa_juridica.sg_estado%type;
qt_mercado_w	integer;
CD_CNPJ_w	varchar(14);
qt_cliente_w	integer;
qt_registro_w	integer;


C01 CURSOR FOR
	SELECT	SG_ESTADO,
		sum(QT_MERCADO)
	from	com_mercado
	group	by SG_ESTADO;


C02 CURSOR FOR
	SELECT	distinct CD_CNPJ
	from	com_mercado_empresa;


BEGIN

delete from eis_market_share;

OPEN C01;
LOOP
FETCH C01 into
	SG_ESTADO_w,
	qt_mercado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	OPEN C02;
	LOOP
	FETCH C02 into
		CD_CNPJ_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

	begin
	select	sum(QT_CLIENTE)
	into STRICT	qt_cliente_w
	from	com_mercado a,
		com_mercado_empresa b
	where	a.nr_sequencia	= b.nr_seq_mercado
	and	a.sg_estado	= sg_estado_w
	and	b.cd_cnpj	= cd_cnpj_W;
	exception
		when others then
			qt_cliente_w	:= 0;
		end;

	select	count(*)
	into STRICT	qt_registro_w
	from	eis_market_share
	where	sg_estado	= sg_estado_w
	and	cd_cnpj		= cd_cnpj_W;

	if (qt_registro_w > 0) then
		update	eis_market_share
		set	qt_cliente	=	qt_cliente + qt_cliente_w,
			qt_mercado	=	qt_mercado + qt_mercado_w
		where	sg_estado	= 	sg_estado_w
		and	cd_cnpj		= 	cd_cnpj_W;

	else
		insert into eis_market_share(
			NR_SEQUENCIA,
			DT_ATUALIZACAO,
			NM_USUARIO,
			CD_CNPJ,
			SG_ESTADO,
			QT_CLIENTE,
			QT_MERCADO )
		values (
			nextval('eis_market_share_seq'),
			clock_timestamp(),
			nm_usuario_p,
			cd_cnpj_W,
			sg_estado_w,
			qt_cliente_w,
			qt_mercado_w);
	end if;

	END LOOP;
	CLOSE C02;

END LOOP;
CLOSE C01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
