SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_eis_reinternacao_uti ( dt_parametro_P timestamp, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_sequencia_w		bigint;
cd_estabelecimento_w		smallint   := 1;
cd_setor_atendimento_w	integer;
nr_atendimento_w		bigint;
ie_sexo_w			varchar(1);
qt_idade_w			smallint;		
qt_cirurgia_eletiva_w	smallint;
qt_cirurgia_Urgencia_w	smallint;
qt_obito_w			smallint;
dt_entrada_w			timestamp;
dt_saida_w			timestamp;
qt_min_perm_w			integer;
qt_apache_w			double precision;
pr_risco_w			double precision;

dt_parametro_w		timestamp;
dt_parametro_fim_w		timestamp;
dt_saida_old_w			timestamp;
ie_inserir_w			boolean;
qt_reg_w			bigint;
dt_entrada_atend_w		timestamp;

C01 CURSOR FOR 
	SELECT t.nr_atendimento, 
		coalesce(p.ie_sexo,'I'), 
		obter_idade(p.dt_nascimento, t.dt_alta,'A') qt_idade, 
		CASE WHEN m.ie_obito='S' THEN 1  ELSE 0 END  qt_obito, 
		trunc(t.dt_entrada) 
	from	Pessoa_fisica p, 
		motivo_alta m, 
		Atendimento_paciente t 
	where	t.dt_entrada between dt_parametro_w and dt_parametro_fim_w 
	and	t.cd_pessoa_fisica	= p.cd_pessoa_fisica 
	and	t.cd_motivo_alta	= m.cd_motivo_alta 
	and	exists (SELECT count(*) 
		from  Setor_atendimento b, 
			atend_paciente_unidade a 
		where  a.nr_atendimento    = t.nr_atendimento 
		and  a.cd_setor_atendimento = b.cd_setor_atendimento 
		and		a.ie_passagem_setor = 'N' 
		and  b.cd_classif_setor = 4 
		having count(*)	>= 2);

 
C02 CURSOR FOR 
 	SELECT		dt_entrada_unidade, 
				dt_saida_unidade 
    from	Setor_atendimento b, 
				atend_paciente_unidade a 
    where	a.nr_atendimento    = nr_atendimento_w 
		and		a.ie_passagem_setor = 'N' 
    and		a.cd_setor_atendimento = b.cd_setor_atendimento 
    and		b.cd_classif_setor = 4 
		order by dt_entrada_unidade asc;

 

BEGIN 
nr_sequencia_w := Gravar_Log_Indicador(806, 'Reinternação UTI', clock_timestamp(), trunc(dt_parametro_p), nm_usuario_p, nr_sequencia_w);
 
dt_parametro_w		:= trunc(dt_parametro_p,'month');
dt_parametro_fim_w	:= last_day(Trunc(dt_parametro_p,'month')) + 86399/86400;
 
delete from eis_paciente_uti 
where dt_referencia	between dt_parametro_w and dt_parametro_fim_w;
COMMIT;
 
delete from EIS_REINTERNACAO_UTI 
where dt_referencia	between dt_parametro_w and dt_parametro_fim_w;
COMMIT;
 
OPEN C01;
LOOP 
FETCH C01 into 
	nr_atendimento_w, 
	ie_sexo_w, 
	qt_idade_w, 
	qt_obito_w, 
	dt_entrada_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	ie_inserir_w	:= false;
	dt_saida_old_w	:= null;
	open C02;
	loop 
	fetch C02 into	 
		dt_entrada_w, 
		dt_saida_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		 
		if (dt_saida_old_w IS NOT NULL AND dt_saida_old_w::text <> '') and 
			(dt_saida_old_w > (dt_entrada_w - 1) ) then 
			 
			 
			select	count(*) 
			into STRICT	qt_reg_w 
			from	atend_paciente_unidade a, 
					setor_atendimento b 
			where	a.nr_atendimento	= nr_atendimento_w 
			and		a.cd_setor_atendimento	= b.cd_setor_atendimento 
			and		a.ie_passagem_setor = 'N' 
			and		b.cd_classif_setor	= '3' 
			and		a.DT_ENTRADA_UNIDADE between dt_saida_old_w and dt_entrada_w;
			 
			if (qt_reg_w	> 0) then 
 
				insert into EIS_REINTERNACAO_UTI(	nr_sequencia, 
									dt_referencia, 
									nm_usuario, 
									dt_atualizacao, 
									nm_usuario_nrec, 
									dt_atualizacao_nrec, 
									nr_atendimento) 
						values (	nextval('eis_reinternacao_uti_seq'), 
									dt_entrada_atend_w, 
									nm_usuario_p, 
									clock_timestamp(), 
									nm_usuario_p, 
									clock_timestamp(), 
									nr_atendimento_w);
			end if;
			 
		end if;
		dt_saida_old_w	:= dt_saida_w;
		end;
	end loop;
	close C02;
	 
	 
	end;
END LOOP;
CLOSE C01;
 
CALL Atualizar_Log_Indicador(clock_timestamp(), nr_sequencia_w);
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_eis_reinternacao_uti ( dt_parametro_P timestamp, nm_usuario_p text) FROM PUBLIC;

