SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_email_prot_doc (nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p
1 - Criação de um protocolo
*/
ds_titulo_w		varchar(255);
ds_comunicacao_w		varchar(4000);
ds_obs_w		varchar(4000);
ie_tipo_protocolo_w		varchar(3);
ds_tipo_protocolo_w	varchar(255);
cd_pessoa_destino_w	varchar(10);
ds_email_destino_w		varchar(2000);
nm_usuario_destino_w	varchar(15);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	select	ie_tipo_protocolo,
		substr(obter_valor_dominio(1047,ie_tipo_protocolo),1,255) ds_tipo_protocolo,
		ds_obs,
		substr(obter_usuario_pessoa(cd_pessoa_destino),1,15) nm_usuario_destino,
		substr(cd_pessoa_destino,1,10)
	into STRICT	ie_tipo_protocolo_w,
		ds_tipo_protocolo_w,
		ds_obs_w,
		nm_usuario_destino_w,
		cd_pessoa_destino_w
	from	protocolo_documento
	where	nr_sequencia = nr_sequencia_p;

	select	max(ds_email)
	into STRICT	ds_email_destino_w
	from	usuario
	where	cd_pessoa_fisica = cd_pessoa_destino_w
	and	ie_situacao = 'A';

	if (ie_opcao_p = '1') then
		ds_titulo_w	:= wheb_mensagem_pck.get_texto(306609); --'Criação de um protocolo documento';
		ds_comunicacao_w := substr( wheb_mensagem_pck.get_texto(306610,'NR_SEQUENCIA_P=' || nr_sequencia_p) || chr(13) || chr(10) ||	--'Criado o protocolo documento nº ' || nr_sequencia_p || chr(13) || chr(10) ||
					wheb_mensagem_pck.get_texto(306612,'DS_TIPO_PROTOCOLO=' || ds_tipo_protocolo_w) || chr(13) || chr(10) || --'Tipo protocolo: ' || ds_tipo_protocolo_w || chr(13) || chr(10) ||
					'Obs.: ' || ds_obs_w,1,4000);
	end if;

	if (coalesce(ds_email_destino_w,'X') <> 'X') then
		CALL enviar_email(	ds_titulo_w,
				ds_comunicacao_w,
				null,
				ds_email_destino_w,
				nm_usuario_p,
				'M');
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_email_prot_doc (nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

