SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_email_solic_transf ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_tipo_mensagem_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		smallint;
ds_mensagem_w			varchar(2000);
ds_assunto_w			varchar(255);
ds_email_adicional_w		varchar(2000);
ds_email_origem_w			varchar(2000);
nr_sequencia_w			bigint;
qt_regra_usuario_w			bigint;
nr_seq_regra_w			bigint;
cd_setor_atendimento_w		integer;
ie_urgente_w			varchar(1);
cd_evento_w			smallint;
cd_perfil_w			varchar(10);
cd_comprador_w			varchar(10);
ds_usuario_origem_w		varchar(255);
ie_usuario_w			varchar(3);
ie_momento_envio_w		varchar(1);
ds_email_remetente_w		varchar(255);

/*
40 - Controle transferência de estoques - Liberação aprovação
41 - Controle transferência de estoques - Estorno da Liberação
42 - Controle transferência de estoques - Cancelamento de um item (Baixa Incorreta ou cancelamento de Entrega)
43 - Controle transferência de estoques - Atendimento da solicitação - geração NF Saída
44 - Controle transferência de estoques - Atendimento da solicitação - geração NF Entrada
*/
c01 CURSOR FOR
SELECT	nr_sequencia
from	regra_envio_email_compra
where	ie_situacao = 'A'
and	ie_tipo_mensagem 		= ie_tipo_mensagem_p
and	cd_estabelecimento 	= cd_estabelecimento_w
and (coalesce(cd_perfil_disparar::text, '') = '' or cd_perfil_disparar = obter_perfil_ativo)
and	obter_se_envia_email_regra(nr_ordem_compra_p, 'TR', ie_tipo_mensagem_p, cd_estabelecimento_w) = 'S';


BEGIN
select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from 	ordem_compra
where 	nr_ordem_compra = nr_ordem_compra_p;

open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	substr(obter_dados_email_solic_transf( nr_ordem_compra_p, nr_item_oci_p, nr_sequencia, 'T'),1,255),
		substr(obter_dados_email_solic_transf( nr_ordem_compra_p, nr_item_oci_p, nr_sequencia, 'M'),1,2000),
		coalesce(ds_email_remetente,'X'),
		replace(ds_email_adicional,',',';'),
		coalesce(ie_usuario,'U'),
		coalesce(ie_momento_envio,'I')
	into STRICT	ds_assunto_w,
		ds_mensagem_w,
		ds_email_remetente_w,
		ds_email_adicional_w,
		ie_usuario_w,
		ie_momento_envio_w
	from	regra_envio_email_compra
	where	nr_sequencia = nr_seq_regra_w;

	if (ie_usuario_w = 'U') then --Usuario
		select	ds_email,
			nm_usuario
		into STRICT	ds_email_origem_w,
			ds_usuario_origem_w
		from	usuario
		where	nm_usuario = nm_usuario_p;
	elsif (ie_usuario_w = 'C') then --Setor compras
		select	ds_email
		into STRICT	ds_email_origem_w
		from	parametro_compras
		where	cd_estabelecimento = cd_estabelecimento_w;

		select	coalesce(ds_fantasia,ds_razao_social)
		into STRICT	ds_usuario_origem_w
		from	estabelecimento_v
		where	cd_estabelecimento = cd_estabelecimento_w;
	elsif (ie_usuario_w = 'O') then --Comprador
		begin
		select	coalesce(max(cd_comprador_padrao),'X')
		into STRICT	cd_comprador_w
		from	parametro_compras
		where	cd_estabelecimento = cd_estabelecimento_w;

		if (cd_comprador_w <> 'X') then
			select	ds_email,
				nm_guerra
			into STRICT	ds_email_origem_w,
				ds_usuario_origem_w
			from	comprador
			where	cd_pessoa_fisica		= cd_comprador_w
			and	cd_estabelecimento		= cd_estabelecimento_w;
		end if;
		end;
	end if;

	if (ds_email_remetente_w <> 'X') then
		ds_email_origem_w	:= ds_email_remetente_w;
	end if;

	if (ie_momento_envio_w = 'A') then
		begin

		CALL sup_grava_envio_email(
			'CT',
			to_char(ie_tipo_mensagem_p),
			nr_ordem_compra_p,
			null,
			null,
			ds_email_adicional_w,
			ds_usuario_origem_w,
			ds_email_origem_w,
			ds_assunto_w,
			ds_mensagem_w,
			cd_estabelecimento_w,
			nm_usuario_p);

		end;
	else
		begin
		CALL enviar_email(ds_assunto_w,ds_mensagem_w,ds_email_origem_w,ds_email_adicional_w,ds_usuario_origem_w,'M');
		exception when others then
			ds_assunto_w := '';
		end;
	end if;

	end;
end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_email_solic_transf ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_tipo_mensagem_p bigint, nm_usuario_p text) FROM PUBLIC;

