SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_empenho_tit_pagar2 (nr_titulo_p text, cd_centro_custo_p bigint, cd_conta_contabil_p text, vl_empenho_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


cd_estabelecimento_w		bigint;
dt_emissao_w			timestamp;
qt_registro_w			bigint;
cd_grupo_w				bigint;

c01 CURSOR FOR
SELECT	b.cd_centro_custo,
	b.cd_conta_contabil,
	coalesce(sum(b.vl_titulo),0) vl_titulo
from	ctb_grupo_conta d,
	conta_contabil c,
	titulo_pagar_classif b,
	titulo_pagar a
where	a.nr_titulo		= b.nr_titulo
and	c.cd_conta_contabil	= b.cd_conta_contabil
and	d.cd_grupo		= c.cd_grupo
and	d.ie_tipo in ('R','D')
and	d.ie_debito_credito	= 'D'
and	a.ie_situacao		<> 'C'
and	coalesce(a.nr_seq_nota_fiscal::text, '') = ''
and	(b.cd_centro_custo IS NOT NULL AND b.cd_centro_custo::text <> '')
and	a.nr_titulo		= nr_titulo_p
group by b.cd_centro_custo,
	b.cd_conta_contabil;

vet01	C01%Rowtype;

c02 CURSOR FOR
SELECT	a.dt_referencia,
	a.cd_estabelecimento,
	a.cd_centro_custo,
	a.cd_conta_contabil
from	ctb_orc_doc_empenho a
where	a.nr_titulo = nr_titulo_p
and	a.cd_centro_custo   = cd_centro_custo_p
and	a.cd_conta_contabil = cd_conta_contabil_p;

vet02	C02%Rowtype;


BEGIN

open C02;
loop
fetch C02 into
	vet02;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	delete from ctb_orc_doc_empenho
	where	nr_titulo	= nr_titulo_p
	and	cd_centro_custo		= vet02.cd_centro_custo
	and	cd_conta_contabil	= vet02.cd_conta_contabil
	and	dt_referencia		= vet02.dt_referencia
	and	cd_estabelecimento	= vet02.cd_estabelecimento;

	CALL ctb_atualizar_saldo_empenho(	vet02.cd_estabelecimento,
					vet02.dt_referencia,
					vet02.cd_centro_custo,
					vet02.cd_conta_contabil,
					nm_usuario_p);

	end;
end loop;
close C02;


if (ie_opcao_p <> 2) then
	if (cd_centro_custo_p IS NOT NULL AND cd_centro_custo_p::text <> '') then
	select	cd_estabelecimento,
			dt_emissao
	into STRICT	cd_estabelecimento_w,
			dt_emissao_w
	from	titulo_pagar
	where	nr_titulo	= nr_titulo_p;

	select	a.cd_grupo
	into STRICT	cd_grupo_w
	from	conta_contabil a
	where	a.cd_conta_contabil = cd_conta_contabil_p;

	if (cd_grupo_w IS NOT NULL AND cd_grupo_w::text <> '') then
		select 	count(*)
		into STRICT	qt_registro_w
		from	ctb_grupo_conta a
		where	a.ie_tipo in ('R','D')
		and		a.ie_debito_credito	= 'D'
		and		a.cd_grupo = cd_grupo_w;

		if (qt_registro_w > 0 ) then
				select 	count(*)
				into STRICT	qt_registro_w
				from	titulo_pagar a
				where	a.nr_titulo = nr_titulo_p
				and	a.ie_situacao		<> 'C'
				and	coalesce(a.nr_seq_nota_fiscal::text, '') = '';

				if (qt_registro_w > 0) then
					CALL ctb_registrar_doc_empenho(cd_estabelecimento_w, trunc(dt_emissao_w,'mm'), null, null, null, nr_titulo_p, null,
											  cd_centro_custo_p, cd_conta_contabil_p, vl_empenho_p, 'N','I',nm_usuario_p);
				end if;
		end if;

	end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_empenho_tit_pagar2 (nr_titulo_p text, cd_centro_custo_p bigint, cd_conta_contabil_p text, vl_empenho_p bigint, nm_usuario_p text, ie_opcao_p text) FROM PUBLIC;

