CREATE OR REPLACE PROCEDURE gerar_empresa_bloqueio ( cd_empresa_p text, ie_empresa_p text, cd_convenio_p bigint, cd_categoria_p text, ie_categoria_p text, nm_usuario_p text) AS $body$
DECLARE


cd_empresa_w	bigint;
cd_categoria_w	integer;

C01 CURSOR FOR
	SELECT	coalesce(cd_categoria,0)
	from	categoria_convenio
	where	cd_convenio = cd_convenio_p
	and	(((ie_categoria_p = '1') and (obter_se_contido(cd_convenio||cd_categoria, elimina_aspas(cd_categoria_p)) = 'S')) or
		((ie_categoria_p = '2') and (not obter_se_contido(cd_convenio||cd_categoria, elimina_aspas(cd_categoria_p)) = 'S')))
	order by	cd_categoria;

C02 CURSOR FOR
	SELECT	coalesce(cd_empresa,0)
	from	empresa_referencia
	where	(((ie_empresa_p = '1') and (obter_se_contido(cd_empresa, elimina_aspas(cd_empresa_p)) = 'S')) or
		((ie_empresa_p = '2') and (not obter_se_contido(cd_empresa, elimina_aspas(cd_empresa_p)) = 'S')));


BEGIN

open C01;
loop
fetch C01 into
	cd_categoria_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (cd_categoria_w > 0) then
		open C02;
		loop
		fetch C02 into
			cd_empresa_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			if (cd_empresa_w > 0) then
				insert into	convenio_empresa_bloqueio(
					nr_sequencia,
					cd_convenio,
					cd_categoria,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_empresa)
				values (nextval('convenio_empresa_bloqueio_seq'),
					cd_convenio_p,
					cd_categoria_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_empresa_w);
			end if;
			end;
		end loop;
		close C02;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
