CREATE OR REPLACE PROCEDURE gerar_empresa_cod_usuario ( cd_convenio_p bigint, cd_usuario_p text, nm_usuario_p text, cd_empresa_p INOUT bigint, nr_sequencia_p INOUT bigint, ie_enviar_email_p INOUT text, ds_email_p INOUT text) AS $body$
DECLARE

			

cd_usuario_convenio_w	conv_usuario_empresa.cd_usuario_convenio%type;
cd_empresa_w		conv_usuario_empresa.cd_empresa%type := 0;
i			bigint;
ie_retorno_w		varchar(1);
ie_enviar_email_w	conv_usuario_empresa.ie_enviar_email%type;
qt_tam_regra_w		bigint;
qt_tam_campo_w		bigint;
nr_sequencia_w		conv_usuario_empresa.nr_sequencia%type := 0;
ds_email_w		conv_usuario_empresa.ds_email%type;
			
C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_usuario_convenio,
		cd_empresa,
		ie_enviar_email,
		ds_email
	from	conv_usuario_empresa
	where	cd_convenio = cd_convenio_p
	and	ie_situacao = 'A'
	order by dt_atualizacao desc;

c01_w  C01%rowtype;


BEGIN

if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and (cd_usuario_p IS NOT NULL AND cd_usuario_p::text <> '') then
	
	qt_tam_campo_w	:= length(cd_usuario_p);
	
	for c01_w in C01 loop
		begin	
		nr_sequencia_w          := c01_w.nr_sequencia;
		cd_usuario_convenio_w   := c01_w.cd_usuario_convenio;
		cd_empresa_w            := c01_w.cd_empresa;
		ie_enviar_email_w       := c01_w.ie_enviar_email;
		ds_email_w              := c01_w.ds_email;
	
		qt_tam_regra_w  := length(cd_usuario_convenio_w);
		
		if (qt_tam_regra_w = qt_tam_campo_w) then
			ie_retorno_w := 'S';
			for i in 1..qt_tam_campo_w loop
				begin
				if (substr(cd_usuario_convenio_w, i, 1) <> '@') and (substr(cd_usuario_convenio_w, i, 1) <> substr(cd_usuario_p, i, 1)) then
					ie_retorno_w := 'N';
					cd_empresa_w := null;
					nr_sequencia_w := null;
					exit;
				end if;
        		end;
		    end loop;

			if (ie_retorno_w = 'S') and (cd_empresa_w IS NOT NULL AND cd_empresa_w::text <> '') then
				cd_empresa_p 	:= cd_empresa_w;
				nr_sequencia_p	:= nr_sequencia_w;
				ie_enviar_email_p	:= ie_enviar_email_w;
				ds_email_p	:= ds_email_w;
				exit;
			end if;
			
		end if;
		end;
	end loop;	
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
