CREATE OR REPLACE PROCEDURE gerar_encaixe_agenda_cirur ( nr_atendimento_p bigint, cd_medico_p text, nr_seq_proc_interno_p bigint, cd_convenio_p bigint, cd_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_agenda_p INOUT bigint) AS $body$
DECLARE

 
 
cd_pessoa_fisica_w	varchar(10);
nr_seq_classif_w	bigint;
qt_min_cirugia_w	smallint:= null;
nr_sequencia_W		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;


BEGIN 
 
select	obter_pessoa_atendimento(nr_atendimento_p,'C') 
into STRICT	cd_pessoa_fisica_w
;
 
nr_seq_classif_w := Obter_Classif_Encaixe_Agenda(nr_seq_classif_w);
 
select	max(coalesce(b.qt_min_cirugia,a.qt_min_cirurgia)) 
into STRICT	qt_min_cirugia_w 
from	proc_interno a, 
	proc_interno_tempo b 
where	a.nr_sequencia 	  			= b.nr_seq_proc_interno 
and	coalesce(b.cd_medico,coalesce(cd_medico_p,'X'))	= coalesce(cd_medico_p,'X') 
and	b.nr_seq_proc_interno			= nr_seq_proc_interno_p 
and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p;
 
if (coalesce(qt_min_cirugia_w::text, '') = '') then 
	qt_min_cirugia_w := 0;
end if;
 
SELECT * FROM Obter_Proc_Tab_Inter_Agenda(nr_seq_proc_interno_p, null, cd_convenio_p, null, null, cd_estabelecimento_p, clock_timestamp(), null, null, null, null, null, null, null, null, cd_procedimento_w, ie_origem_proced_w) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
 
select	nextval('agenda_paciente_seq') 
into STRICT	nr_sequencia_W
;
 
nr_seq_agenda_p	:= nr_sequencia_W;
 
insert into agenda_paciente( 
	nr_sequencia, 
	cd_agenda, 
	cd_pessoa_fisica, 
	dt_agenda, 
	hr_inicio, 
	nr_minuto_duracao, 
	nm_usuario, 
	dt_atualizacao, 
	cd_medico, 
	ie_status_agenda, 
	nr_seq_classif_agenda, 
	nr_atendimento, 
	dt_agendamento, 
	nr_seq_proc_interno, 
	ie_equipamento, 
	cd_procedimento, 
	ie_origem_proced, 
	cd_convenio) 
values ( 
	nr_sequencia_w, 
	cd_agenda_p, 
	cd_pessoa_fisica_w, 
	trunc(clock_timestamp(),'dd'), 
	trunc(clock_timestamp(),'mi'), 
	qt_min_cirugia_w, 
	nm_usuario_p, 
	clock_timestamp(), 
	cd_medico_p, 
	'N', 
	nr_seq_classif_w, 
	nr_atendimento_p, 
	clock_timestamp(), 
	nr_seq_proc_interno_p, 
	'N', 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	cd_convenio_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
