SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_encaixe_agenda_consulta (cd_estabelecimento_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp, hr_encaixe_p text, qt_duracao_p bigint, cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, ds_observacao_p text, nm_usuario_p text, nr_seq_encaixe_p INOUT bigint) AS $body$
DECLARE


dt_encaixe_w			timestamp;
ds_consistencia_w		varchar(255);
cd_turno_w			varchar(1);
cd_classificacao_w		varchar(5);
ie_forma_convenio_w		varchar(2);
cd_convenio_w			integer;
cd_categoria_w		varchar(10);
cd_usuario_convenio_w	varchar(30);
dt_validade_w			timestamp;
nr_doc_convenio_w		varchar(20);
cd_tipo_acomodacao_w		smallint;
nr_seq_agenda_w		agenda_consulta.nr_sequencia%type;


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (hr_encaixe_p IS NOT NULL AND hr_encaixe_p::text <> '') and (qt_duracao_p IS NOT NULL AND qt_duracao_p::text <> '') and
	((cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') or (nm_pessoa_fisica_p IS NOT NULL AND nm_pessoa_fisica_p::text <> '')) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	/* obter horario agenda x encaixe */

	dt_encaixe_w := to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' || hr_encaixe_p || ':00','dd/mm/yyyy hh24:mi:ss');	


	/* obter turno */

	select	obter_turno_horario_agenda(cd_agenda_p, dt_encaixe_w)
	into STRICT	cd_turno_w
	;


	/* obter classificacao */


	/*
	obter_classif_encaixe_agecons(cd_classificacao_w);
	*/


	/* obter sequencia */

	select	nextval('agenda_consulta_seq')
	into STRICT	nr_seq_agenda_w
	;

	/* gerar encaixe */

	insert into agenda_consulta(
					nr_sequencia,
					cd_agenda,
					dt_agenda,
					nr_minuto_duracao,
					ie_status_agenda,
					ie_classif_agenda,
					cd_pessoa_fisica,
					nm_paciente,
					nr_telefone,
					cd_convenio,
					cd_medico,
					cd_procedimento,
					ie_origem_proced,
					nr_seq_proc_interno,
					ds_observacao,
					cd_turno,
					dt_agendamento,
					nm_usuario_origem,
					nm_usuario,
					dt_atualizacao,
					ie_encaixe
					)
				values (
					nr_seq_agenda_w,
					cd_agenda_p,
					dt_encaixe_w,
					qt_duracao_p,
					'N',
					cd_classificacao_w,
					cd_pessoa_fisica_p,
					substr(coalesce(obter_nome_pf(cd_pessoa_fisica_p), nm_pessoa_fisica_p),1,60),
					substr(obter_fone_pac_agenda(cd_pessoa_fisica_p),1,255),
					null,
					null, 
					null, 
					null, 
					null, 
					ds_observacao_p,
					cd_turno_w,
					clock_timestamp(),
					nm_usuario_p,
					nm_usuario_p,
					clock_timestamp(),
					'S'
					);


end if;

delete 	FROM agenda_controle_horario
where 	cd_agenda = cd_agenda_p
and 	dt_agenda = trunc(dt_agenda_p);

commit;

nr_seq_encaixe_p := nr_seq_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
