CREATE OR REPLACE PROCEDURE gerar_entrada_real_barras_int ( nr_atendimento_p bigint, dt_entrada_real_p timestamp) AS $body$
DECLARE

		 
nr_atendimento_w	bigint	:= 0;
cd_unidade_w		varchar(255);
cd_setor_atendimento_w	varchar(255);
cd_unidade_basica_w	varchar(255);
cd_unidade_compl_w	varchar(255);
	

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
 
	select	nr_atendimento 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente 
	where	nr_atendimento	= nr_atendimento_p;
	 
	cd_unidade_w	:= substr(obter_unidade_atendimento(nr_atendimento_p, 'A', 'NR'),1,255);
 
	if (nr_atendimento_w > 0) and (cd_unidade_w > 0) then 
		begin 
		 
		cd_setor_atendimento_w	:= substr(obter_unidade_atendimento(nr_atendimento_p, 'A', 'CS'),1,255);
		cd_unidade_basica_w	:= substr(obter_unidade_atendimento(nr_atendimento_p, 'A', 'UB'),1,255);
		cd_unidade_compl_w	:= substr(obter_unidade_atendimento(nr_atendimento_p, 'A', 'UC'),1,255);
		 
		update	atend_paciente_unidade 
		set	dt_entrada_real		= CASE WHEN dt_entrada_real_p = NULL THEN clock_timestamp()  ELSE dt_entrada_real_p END  
		where	nr_atendimento 		= nr_atendimento_w 
		and	cd_setor_atendimento	= cd_setor_atendimento_w 
        and	cd_unidade_basica	= cd_unidade_basica_w 
        and	cd_unidade_compl	= cd_unidade_compl_w 
        and	coalesce(dt_saida_unidade::text, '') = '' 
		and	coalesce(dt_entrada_real::text, '') = '';		
		 
		commit;
 
		end;
	end if;
	 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
