SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_entrada_setor_prescr ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, dt_passagem_p timestamp, nm_usuario_p text) AS $body$
DECLARE

				 
cd_unidade_basica_w	varchar(10);
cd_unidade_compl_w	varchar(10);
cd_tipo_acomodacao_w	bigint;

nr_seq_unidade_w	bigint;
ds_setor_atendimento_w	varchar(100);
				

BEGIN 
 
select	coalesce(min(nr_seq_interno),0) 
into STRICT	nr_seq_unidade_w 
from	unidade_atendimento 
where	ie_situacao = 'A' 
and	coalesce(nr_atendimento::text, '') = '' 
and	cd_setor_atendimento in (SELECT	cd_setor_atendimento 
	from	usuario_setor_v 
	where	upper(nm_usuario)	= upper(nm_usuario_p) 
	and	cd_setor_atendimento	= cd_setor_atendimento_p);
 
if (nr_seq_unidade_w = 0) then 
	select	substr(obter_desc_expressao(cd_exp_setor_atend,DS_SETOR_ATENDIMENTO),1,100) 
	into STRICT	ds_setor_atendimento_w 
	from	setor_atendimento 
	where	cd_setor_atendimento	= cd_setor_atendimento_p;
	 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort( 262422 , 'DS_SETOR_ATENDIMENTO_W='||ds_setor_atendimento_w||';NM_USUARIO_P='||nm_usuario_p);
end if;
		 
		 
select	cd_unidade_basica, 
	cd_unidade_compl, 
	cd_tipo_acomodacao 
into STRICT	cd_unidade_basica_w, 
	cd_unidade_compl_w, 
	cd_tipo_acomodacao_w 
from	unidade_atendimento 
where	nr_seq_interno	= nr_seq_unidade_w;
	 
			 
CALL Gerar_Transferencia_Paciente(	nr_atendimento_p, cd_setor_atendimento_p, 
				cd_unidade_basica_w, cd_unidade_compl_w, 
				cd_tipo_acomodacao_w, 0, null,	'', 
				nm_usuario_p, dt_passagem_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
