SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_equipamento_gases (nr_seq_pepo_p bigint, nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_equipamento_w 	bigint;
cd_profissional_w	varchar(10);
dt_fim_w		timestamp;
dt_inicio_w		timestamp;
nr_seq_equipamento_w	bigint;

c01 CURSOR FOR
	SELECT	b.cd_equipamento,
		a.cd_profissional,
		c.dt_inicio_adm,
		c.dt_final_adm
	from	agente_anestesico b,
		cirurgia_agente_anestesico a,
		cirurgia_agente_anest_ocor c
	where 	c.nr_seq_cirur_agente = a.nr_sequencia
	and	a.nr_seq_agente	= b.nr_sequencia
	and	((a.nr_seq_pepo = nr_seq_pepo_p) or (a.nr_cirurgia = nr_cirurgia_p))
	and 	(b.cd_equipamento IS NOT NULL AND b.cd_equipamento::text <> '')
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(c.ie_situacao,'A') = 'A';
	

BEGIN

delete FROM equipamento_cirurgia
where	((nr_seq_pepo = nr_seq_pepo_p) or (nr_cirurgia = nr_cirurgia_p))
and 	ie_gerado_sistema = 'S'
and 	coalesce(ie_situacao,'A') = 'A';
commit;

open c01;
loop
fetch c01 into	
	cd_equipamento_w,
	cd_profissional_w,
	dt_inicio_w,
	dt_fim_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	Select 	nextval('equipamento_cirurgia_seq')
	into STRICT	nr_seq_equipamento_w
	;

	insert into equipamento_cirurgia(
		nr_sequencia,
		cd_equipamento,
		dt_lancamento_automatico,
		nr_seq_pepo,
		nr_cirurgia,
		cd_profissional,
		dt_atualizacao,
		nm_usuario,
		qt_equipamento,
		dt_fim,
		dt_inicio,
		ie_gerado_sistema,
		ie_situacao)
	values (nr_seq_equipamento_w,
		cd_equipamento_w,
		clock_timestamp(),
		CASE WHEN nr_seq_pepo_p=0 THEN null  ELSE nr_seq_pepo_p END ,
		nr_cirurgia_p,
		cd_profissional_w,
		clock_timestamp(),
		nm_usuario_p,
		1,
		dt_fim_w,
		dt_inicio_w,
		'S',
		'A');
	commit;
	CALL gerar_taxa_equipamento(wheb_usuario_pck.get_cd_estabelecimento,nr_seq_equipamento_w,nm_usuario_p);
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
