CREATE OR REPLACE PROCEDURE gerar_equipe_medica_cirurgia ( nr_cirurgia_p bigint, ie_tipo_p bigint, nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(15):=null;
ie_funcao_w		varchar(15):=null;
nr_sequencia_w		bigint;


BEGIN

if (ie_tipo_p = 1) then
	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	pf_equipe_partic
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_tipo_p = 2) then
	select	cd_pessoa_fisica,
		ie_funcao
	into STRICT	cd_pessoa_fisica_w,
		ie_funcao_w
	from	cirurgia_participante
	where	nr_seq_interno = nr_sequencia_p;
elsif (ie_tipo_p = 3) then
	select	cd_anestesista
	into STRICT	cd_pessoa_fisica_w
	from	agenda_paciente
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_tipo_p = 4) then
	cd_pessoa_fisica_w := nr_sequencia_p;
elsif (ie_tipo_p = 5) then
	select	cd_pessoa_fisica,
		ie_funcao
	into STRICT	cd_pessoa_fisica_w,
		ie_funcao_w
	from	cirurgia_participante
	where	nr_seq_interno = nr_sequencia_p;
end if;

if (coalesce(ie_funcao_w::text, '') = '') then
	select	max(ie_funcao)
	into STRICT	ie_funcao_w
	from	cirurgia_participante
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;
	if (coalesce(ie_funcao_w::text, '') = '') then
		select	max(cd_funcao)
		into STRICT	ie_funcao_w
		from	funcao_medico
		where	ie_medico = 'S';
	end if;
end if;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') and (ie_funcao_w IS NOT NULL AND ie_funcao_w::text <> '') and (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then

	select	coalesce(max(nr_sequencia),0)+1
	into STRICT	nr_sequencia_w
	from	cirurgia_participante
	where	nr_cirurgia = nr_cirurgia_p;

	insert	into 	cirurgia_participante(
			nr_cirurgia,
			nr_sequencia,
			nr_seq_interno,
			nm_usuario,
			dt_atualizacao,
			cd_pessoa_fisica,
			ie_situacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_funcao)

	SELECT		nr_cirurgia_p,
			nr_sequencia_w,
			nextval('cirurgia_participante_seq'),
			nm_usuario_p,
			clock_timestamp(),
			cd_pessoa_fisica_w,
			'A',
			clock_timestamp(),
			nm_usuario_p,
			ie_funcao_w
	;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
