CREATE OR REPLACE PROCEDURE gerar_equip_agenda_cirurgia (nr_prescricao_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_equipamento_w	bigint;
nr_sequencia_w		bigint;

c01 CURSOR FOR
	SELECT 	a.cd_equipamento
	from   	agenda_pac_equip a
	where  	a.nr_seq_agenda = nr_seq_agenda_p
	and	(a.cd_equipamento IS NOT NULL AND a.cd_equipamento::text <> '')
	
union all

	SELECT 	b.cd_equipamento
	from   	agenda_pac_equip a,
		equipamento b
	where  	a.nr_seq_classif_equip 	= b.cd_classificacao
	and	a.nr_seq_agenda 	= nr_seq_agenda_p
	and	(b.cd_equipamento IS NOT NULL AND b.cd_equipamento::text <> '');


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_seq_agenda_p,0) > 0) then
	open c01;
	loop
	fetch c01	into
		cd_equipamento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	prescr_equipamento
	where	nr_prescricao = nr_prescricao_p;

	insert	into	prescr_equipamento(
			nr_prescricao,
			nr_sequencia,
			cd_equipamento,
			dt_atualizacao,
			nm_usuario)
	values (nr_prescricao_p,
			nr_sequencia_w,
			cd_equipamento_w,
			clock_timestamp(),
			nm_usuario_p);
	end;
	end loop;
	close c01;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
