CREATE OR REPLACE PROCEDURE gerar_equip_agenda_proc (nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_equipamento_w		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_equip_w		bigint;

c01 CURSOR FOR
	SELECT	a.cd_equipamento
	from	procedimento_equip a
	where	a.cd_procedimento	= cd_procedimento_w
	and	a.ie_origem_proced	= ie_origem_proced_w;


BEGIN

select	coalesce(max(cd_procedimento),0),
	coalesce(max(ie_origem_proced),0)
into STRICT	cd_procedimento_w,
	ie_origem_proced_w
from	agenda_paciente
where	nr_sequencia	= nr_seq_agenda_p;

if (cd_procedimento_w > 0) then
	begin

	open	c01;
	loop
	fetch	c01 into cd_equipamento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	nextval('agenda_pac_equip_seq')
		into STRICT	nr_seq_equip_w
		;

		insert	into agenda_pac_equip(nr_sequencia,
			nr_seq_agenda,
			dt_atualizacao,
			nm_usuario,
			cd_equipamento,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (nr_seq_equip_w,
			nr_seq_agenda_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_equipamento_w,
			clock_timestamp(),
			nm_usuario_p);
		end;
	end loop;
	close c01;

	end;
end if;


commit;

end;
