CREATE OR REPLACE PROCEDURE gerar_equip_prescr_cirurgia ( cd_equipamento_p bigint, nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN
if (cd_equipamento_p IS NOT NULL AND cd_equipamento_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	coalesce(max(nr_sequencia),0)+1
	into STRICT	nr_sequencia_w
	from	prescr_equipamento
	where	nr_prescricao = nr_prescricao_p;

	insert	into	prescr_equipamento(nr_sequencia,
			nr_prescricao,
			cd_equipamento,
			dt_atualizacao,
			nm_usuario,
			cd_motivo_baixa,
			dt_baixa)
	values (nr_sequencia_w,
			nr_prescricao_p,
			cd_equipamento_p,
			clock_timestamp(),
			nm_usuario_p,
			null,
			null);
	commit;
end if;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
