SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_erro_cpoe (nr_seq_item_p bigint, ie_tipo_item_p text, nr_regra_p bigint, ie_permite_liberar_p text, ds_compl_erro_p text, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_erro_p INOUT bigint) AS $body$
DECLARE


nr_seq_erro_w		bigint;
ie_regra_w		varchar(15);
ds_regra_w    varchar(255);
ds_msg_perfil_w    varchar(4000);
ds_msg_wheb_w    varchar(4000);
ds_inf_w    varchar(2000);
ie_prioridade_w    smallint;

c01 CURSOR FOR
SELECT  ds_mensagem_cliente
from  regra_consiste_prescr_par
where  nr_seq_regra       = nr_regra_p
and  coalesce(cd_perfil,cd_perfil_p)  = cd_perfil_p
order by
  nr_sequencia;


BEGIN

if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (ie_permite_liberar_p IS NOT NULL AND ie_permite_liberar_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

  open c01;
  loop
  fetch c01 into ds_msg_perfil_w;
  EXIT WHEN NOT FOUND; /* apply on c01 */
    begin
    ds_msg_perfil_w := ds_msg_perfil_w;
    end;
  end loop;
  close c01;

  select  ie_tipo_regra,
    obter_desc_expressao(cd_exp_descricao,ds_regra_prescr),
    obter_desc_expressao(cd_exp_mensagem,ds_mensagem_usuario),
    obter_desc_expressao(cd_exp_informacao_adic,ds_informacao_adic),
    ie_prioridade
  into STRICT  ie_regra_w,
    ds_regra_w,
    ds_msg_wheb_w,
    ds_inf_w,
    ie_prioridade_w
  from  regra_consiste_prescr
  where  nr_sequencia = nr_regra_p;

  select  nextval('cpoe_inconsistency_seq')
  into STRICT  nr_seq_erro_w
;

  CALL gravar_log_cpoe(substr('GERAR_ERRO_CPOE INSERT INCONSISTENCIA: '
                        || 'nr_seq_item_p: ' || nr_seq_item_p
                        || '; ie_tipo_item_p: ' || ie_tipo_item_p
                        || '; nr_regra_p: ' || nr_regra_p
                        || '; ie_permite_liberar_p: ' || ie_permite_liberar_p
                        || '; ds_compl_erro_p: ' || ds_compl_erro_p
                        || '; cd_perfil_p: ' || cd_perfil_p
                        || '; nm_usuario_p: ' || nm_usuario_p
                        || '; nr_atendimento_p: ' || nr_atendimento_p
                        || '; nr_prescricao_p: ' || nr_prescricao_p
						|| '; nr_seq_erro_w: ' || nr_seq_erro_w
                        || '; ds_regra_w: ' || ds_regra_w
                        || '; ds_msg_perfil_w: ' || ds_msg_perfil_w
                        || '; ds_msg_wheb_w: ' || ds_msg_wheb_w
                        || '; ie_regra_w: ' || ie_regra_w 
                        || '; ds_inf_w: ' || ds_inf_w
                        || '; ie_prioridade_w: ' || ie_prioridade_w
                        || '; ds_stack: ' || dbms_utility.format_call_stack, 1,2000),
				nr_atendimento_p);

  insert into cpoe_inconsistency(
    nr_sequencia,
    nr_seq_item,
    nr_regra,
    ds_inconsistencia,
    ds_erro,
    ds_compl_erro,
    ie_libera,
    ie_tipo_regra,
    ds_inf_adicional,
    ie_prioridade,
    nm_usuario_nrec,
    dt_atualizacao_nrec,
    nm_usuario,
    dt_atualizacao,
    ie_tipo_item,
    nr_atendimento,
    nr_prescricao,
    dt_liberacao)
  values (
    nr_seq_erro_w,
    nr_seq_item_p,
    nr_regra_p,
    ds_regra_w,
    coalesce(ds_msg_perfil_w,ds_msg_wheb_w),
    ds_compl_erro_p,
    ie_permite_liberar_p,
    ie_regra_w,
    ds_inf_w,
    ie_prioridade_w,
    nm_usuario_p,
    clock_timestamp(),
    nm_usuario_p,
    clock_timestamp(),
    ie_tipo_item_p,
    nr_atendimento_p,
    nr_prescricao_p,
    null);

    commit;

  nr_seq_erro_p  := nr_seq_erro_w;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_erro_cpoe (nr_seq_item_p bigint, ie_tipo_item_p text, nr_regra_p bigint, ie_permite_liberar_p text, ds_compl_erro_p text, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_erro_p INOUT bigint) FROM PUBLIC;

