CREATE OR REPLACE PROCEDURE gerar_erro_interv_sae ( nr_seq_sae_p bigint, nr_seq_proc_p bigint, nr_regra_p bigint, ie_permite_liberar_p text, ds_compl_erro_p text, cd_perfil_p bigint, nm_usuario_p text, nr_seq_erro_p INOUT bigint, nr_seq_item_p bigint default null, nr_seq_diag_p bigint default null, nr_prescricao_p bigint default null, nr_seq_material_p bigint default null) AS $body$
DECLARE


nr_seq_erro_w		bigint;
ie_regra_w		varchar(15);
ds_regra_w		varchar(255);
ds_msg_perfil_w		varchar(4000);
ds_msg_wheb_w		varchar(4000);
ds_inf_w		varchar(2000);
ie_prioridade_w		smallint;
ds_cor_w		varchar(15);
ie_agrupador_w		smallint;



BEGIN

if (nr_seq_sae_p IS NOT NULL AND nr_seq_sae_p::text <> '') and (ie_permite_liberar_p IS NOT NULL AND ie_permite_liberar_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	/*select	ds_regra_prescr,
		ds_mensagem_usuario,
		ds_informacao_adic
	into	ds_regra_w,
		ds_msg_wheb_w,
		ds_inf_w
	from	regra_consiste_sae
	where	nr_sequencia = nr_regra_p;*/
	select	substr(obter_desc_expressao(cd_exp_regra,ds_regra_prescr),1,255),
			obter_desc_expressao(cd_exp_mensagem,ds_mensagem_usuario),
			obter_desc_expressao(cd_exp_informacao,ds_informacao_adic)
	into STRICT	ds_regra_w,
			ds_msg_wheb_w,
			ds_inf_w
	from	regra_consiste_sae
	where	nr_sequencia = nr_regra_p;


	select	nextval('pe_prescricao_erro_seq')
	into STRICT	nr_seq_erro_w
	;

	insert into pe_prescricao_erro(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						nr_seq_prescr,
						nr_seq_prescr_proc,
						ds_inconsistencia,
						ds_erro,
						ie_libera,
						nr_seq_regra,
						nr_seq_item,
						nr_seq_diag,
						nr_prescricao,
						nr_seq_material)
		values (	nr_seq_erro_w,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_sae_p,
						nr_seq_proc_p,
						ds_regra_w,
						ds_msg_wheb_w,
						ie_permite_liberar_p,
						nr_regra_p,
						nr_seq_item_p,
						nr_seq_diag_p,
						nr_prescricao_p,
						nr_seq_material_p);

end if;
nr_seq_erro_p	:= nr_seq_erro_w;

commit;

end;
$body$
