CREATE OR REPLACE PROCEDURE gerar_erro_pac_prot ( nr_seq_paciente_p bigint, nr_seq_material_p bigint, nr_regra_p bigint, ds_compl_erro_p text, cd_perfil_p bigint, nm_usuario_p text, nr_seq_erro_p INOUT bigint, ds_mensagem_p text default null) AS $body$
DECLARE


nr_seq_erro_w		bigint;
ie_regra_w		varchar(15);
ds_regra_w		varchar(255);
ds_msg_perfil_w		varchar(4000);
ds_msg_wheb_w		varchar(4000);
ds_inf_w		varchar(2000);
ie_prioridade_w		smallint;
ds_cor_w					varchar(15);
ie_agrupador_w				smallint;
ie_forma_consistencia_w		varchar(3);
ie_justificar_consistencia_w	varchar(3);


c01 CURSOR FOR
	SELECT	ie_forma_consistencia,
			DS_MENSAGEM_CLIENTE,
			ie_justificar_consistencia
	from	regra_consiste_onc_par
	where	nr_seq_regra = nr_regra_p
	and	coalesce(cd_perfil,cd_perfil_p) = cd_perfil_p
	order by coalesce(cd_perfil,0);


BEGIN

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	/*select	ds_regra,
		ds_mensagem_usuario,
		ds_informacao_adic
	into	ds_regra_w,
		ds_msg_wheb_w,
		ds_inf_w
	from	regra_consiste_onc
	where	nr_sequencia = nr_regra_p;*/
	select	substr(obter_desc_expressao(cd_exp_regra,ds_regra),1,255),
			obter_desc_expressao(cd_exp_mensagem,ds_mensagem_usuario),
			obter_desc_expressao(cd_exp_informacao,ds_informacao_adic)
	into STRICT	ds_regra_w,
			ds_msg_wheb_w,
			ds_inf_w
	from	regra_consiste_onc
	where	nr_sequencia = nr_regra_p;

	open c01;
	loop
	fetch c01 into
			ie_forma_consistencia_w,
			ds_msg_perfil_w,
			ie_justificar_consistencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;

	select	nextval('paciente_atendimento_erro_seq')
	into STRICT	nr_seq_erro_w
	;

	if (coalesce(ie_forma_consistencia_w,'S') = 'S') and (coalesce(ie_justificar_consistencia_w,'N') = 'S') then

		ie_forma_consistencia_w := 'J';

	end if;

	insert into paciente_atendimento_erro(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						nr_seq_paciente,
						nr_seq_material,
						ds_inconsistencia,
						ds_erro,
						ie_libera,
						nr_seq_regra)
		values (	nr_seq_erro_w,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_paciente_p,
						nr_seq_material_p,
						coalesce(ds_mensagem_p, substr(ds_regra_w||'. '||ds_compl_erro_p,1,255)),
						coalesce(ds_msg_perfil_w,ds_msg_wheb_w),
						coalesce(ie_forma_consistencia_w,'S'),
						nr_regra_p);

end if;
nr_seq_erro_p	:= nr_seq_erro_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
