CREATE OR REPLACE PROCEDURE gerar_erro_prescr_risco (nr_prescricao_p bigint, cd_material_p bigint) AS $body$
DECLARE


nr_seq_risco_w			prescr_medica_risco.nr_sequencia%type;
nr_seq_medic_risco_w	medicamento_risco.nr_sequencia%type;

c01 CURSOR FOR
	SELECT 	b.nr_sequencia
	from 	material a,
			medicamento_risco b,
			prescr_material c
	where 	a.cd_material = cd_material_p
	and 	a.cd_material = b.cd_material
	and 	a.cd_material = c.cd_material
	and 	c.nr_prescricao = nr_prescricao_p
	and		coalesce(b.ie_situacao, 'A') = 'A';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	open c01;
	loop
	fetch c01 into nr_seq_medic_risco_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		select	nextval('prescr_medica_risco_seq')
		into STRICT	nr_seq_risco_w
		;

		insert into prescr_medica_risco(
			nr_sequencia,
			dt_confirmacao,
			ie_ciente,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nr_prescricao,
			nm_usuario_ciente,
			ds_motivo,
			nr_seq_risco)
		values (
			nr_seq_risco_w,
			null,
			'N',
			clock_timestamp(),
			clock_timestamp(),
			nr_prescricao_p,
			null,
			null,
			nr_seq_medic_risco_w);

	end loop;
	close c01;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
