CREATE OR REPLACE PROCEDURE gerar_escala_anestesista_pj ( nr_seq_classif_p bigint, cd_cgc_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

cd_medico_w		varchar(10);
cd_medico_ww    varchar(10);

c01 CURSOR FOR
SELECT	a.cd_medico
from	medico_entidade_juridica a
where	a.cd_cgc = cd_cgc_p
and not exists (select  1
				from  	escala_profissional x
				where 	x.nr_seq_classif = nr_seq_classif_p
				and 	a.cd_medico = x.cd_profissional)
and	ie_situacao = 'A';


BEGIN

open c01;
loop
fetch c01 into cd_medico_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	begin

		insert into escala_profissional(nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						cd_profissional,
						nr_seq_classif)
				values (nextval('escala_profissional_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						cd_medico_w,
						nr_seq_classif_p);

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
