SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_escala_elpo ((nr_sequencia_p bigint, nm_usuario_p text) is nr_atendimento_w bigint) RETURNS varchar AS $body$
DECLARE


	ie_retorno_w	varchar(15);
	nr_seq_result_escala_w	varchar(15);
	
BEGIN
	
	select	max(f.nr_seq_result_escala)
	into STRICT	nr_seq_result_escala_w
	from	pe_prescricao a,
		pe_prescr_item_result b,
		pe_regra_item_escala c,
		pe_regra_result_escala f
	where	a.nr_sequencia = nr_sequencia_p
	and	a.nr_sequencia = b.nr_seq_prescr
	and	b.nr_seq_item = c.nr_seq_item_sae
	and	c.nr_seq_item_escala = nr_seq_item_escala_p
	and	f.nr_seq_result_sae = b.nr_seq_result
	and	f.nr_seq_regra = c.nr_sequencia;
	
	select 	max(x.vl_result)
	into STRICT	ie_retorno_w
	from	pe_result_item x,
		pe_regra_result_escala f
	where	x.nr_sequencia = nr_seq_result_escala_w;
	
	return ie_retorno_w;
	end;
	
	function obter_descricao_escala_elpo( nr_seq_item_escala_p	number) return varchar2 is

	ds_observacao_w	varchar2(80);
	nr_seq_result_escala_w	varchar2(15);
	begin
	
	select	max(b.ds_observacao)
	into STRICT	ds_observacao_w
	from	pe_prescricao a,
		pe_prescr_item_result b,
		pe_regra_item_escala c,
		pe_regra_result_escala f
	where	a.nr_sequencia = nr_sequencia_p
	and	a.nr_sequencia = b.nr_seq_prescr
	and	b.nr_seq_item = c.nr_seq_item_sae
	and	c.nr_seq_item_escala = nr_seq_item_escala_p
	and	f.nr_seq_result_sae = b.nr_seq_result
	and	f.nr_seq_regra = c.nr_sequencia;
	
	return ds_observacao_w;
	end;

begin

select	max(a.cd_prescritor),
	max(a.nr_atendimento)
into STRICT	cd_pessoa_fisica_w,
	nr_atendimento_w
from	pe_prescricao a,
	pe_prescr_item_result b
where	a.nr_sequencia = b.nr_seq_prescr
and	a.nr_sequencia = nr_sequencia_p;

select	coalesce(max('S'),'N')
into STRICT	ie_possui_registro_w
from 	pe_prescricao a,
	pe_prescr_item_result b,
	pe_regra_item_escala c,
	pe_regra_result_escala d
where	a.nr_sequencia	= b.nr_seq_prescr
and	b.nr_seq_result = d.nr_seq_result_sae
and	b.nr_seq_item	= c.nr_seq_item_sae
and	a.nr_sequencia	= nr_sequencia_p
and	(c.nr_seq_item_escala IS NOT NULL AND c.nr_seq_item_escala::text <> '')
and 	c.ie_escala = 179;

if (ie_possui_registro_w = 'S') then

	begin

	ie_tipo_pos_cir_w	:= obter_valor_escala(203);
	ie_tempo_cir_w	:= obter_valor_escala(204);
	ie_tipo_anestesia_w	:=	 obter_valor_escala(205);
	ie_sup_suporte_w	:= obter_valor_escala(206);	
	ie_pos_membros_w	:= obter_valor_escala(207);	
	ie_comorbidades_w	:= obter_valor_escala(208);
	ie_faixa_idade_w	:=	 obter_valor_escala(209);	

end;	

	insert into escala_elpo(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				cd_profissional,
				dt_avaliacao,
				nr_atendimento,
				ie_situacao,
				dt_liberacao,
				ie_sup_suporte,
				ie_pos_membros,
				ie_comorbidades,
				ie_tipo_anestesia,
				ie_faixa_idade,
				ie_tipo_pos_cir,
				ie_tempo_cir)
	values (nextval('escala_elpo_seq'),
				 clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_fisica_w,
				clock_timestamp(),
				nr_atendimento_w,
				'A',
				clock_timestamp(),
				coalesce(ie_sup_suporte_w, 'N'),
				coalesce(ie_pos_membros_w, 'N'),
				coalesce(ie_comorbidades_w, 'N'),
				coalesce(ie_tipo_anestesia_w, 'N'),
				coalesce(ie_faixa_idade_w, 'N'),
				coalesce(ie_tipo_pos_cir_w, 'N'),
				coalesce(ie_tempo_cir_w, 'N'));	
				

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
