SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_escala_partic_cir ( nr_seq_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_registros_w		bigint;
dia_da_semana_w		varchar(100);
hr_inicio_w		timestamp;
hr_fim_w		timestamp;
cd_agenda_w		bigint;
nr_seq_equipe_w		bigint;
cd_funcao_w		smallint;
cd_pessoa_fisica_w	varchar(10);

c01 CURSOR FOR
	SELECT 	a.cd_pessoa_fisica,
		a.nr_seq_equipe,
		a.cd_funcao
	from   	regra_escala_partic_cir a
	where	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(a.cd_agenda,cd_agenda_w) = cd_agenda_w
	and	((coalesce(a.ie_dia_semana,dia_da_semana_w) = dia_da_semana_w) or ((coalesce(a.ie_dia_semana,dia_da_semana_w) = 9) and (dia_da_semana_w not in (7,1))))
	and	hr_inicio_w	between coalesce(a.dt_inicio_agenda,to_date('01/01/1900 00:00:01','dd/mm/yyyy hh24:mi:ss')) and coalesce(a.dt_fim_vigencia,to_date('01/01/2099 23:59:59','dd/mm/yyyy hh24:mi:ss'))
	and	((hr_inicio_w	between	to_date(to_char(hr_inicio_w,'dd/mm/yyyy') || ' ' || coalesce(to_char(a.hr_inicio,'hh24:mi:ss'),'00:00:00'),'dd/mm/yyyy hh24:mi:ss') and
					to_date(to_char(hr_inicio_w,'dd/mm/yyyy') || ' ' || coalesce(to_char(a.hr_fim,'hh24:mi:ss'),'23:59:59'),'dd/mm/yyyy hh24:mi:ss')) or (hr_fim_w  		between to_date(to_char(hr_inicio_w,'dd/mm/yyyy') || ' ' || coalesce(to_char(a.hr_inicio,'hh24:mi:ss'),'00:00:00'),'dd/mm/yyyy hh24:mi:ss') and
					to_date(to_char(hr_inicio_w,'dd/mm/yyyy') || ' ' || coalesce(to_char(a.hr_fim,'hh24:mi:ss'),'23:59:59'),'dd/mm/yyyy hh24:mi:ss')) or
		 ((hr_inicio_w 	< to_date(to_char(hr_inicio_w,'dd/mm/yyyy') || ' ' || coalesce(to_char(a.hr_inicio,'hh24:mi:ss'),'00:00:00'),'dd/mm/yyyy hh24:mi:ss')) and (hr_fim_w > to_date(to_char(hr_inicio_w,'dd/mm/yyyy') || ' ' || coalesce(to_char(a.hr_fim,'hh24:mi:ss'),'23:59:59'),'dd/mm/yyyy hh24:mi:ss'))))
	and	not 	exists (SELECT	1
				from	profissional_agenda x
				where	((x.cd_profissional 	= a.cd_pessoa_fisica) or (x.nr_seq_equipe	= a.nr_seq_equipe))
				and	x.nr_seq_agenda		= nr_seq_agenda_p);


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	select 	count(*)
	into STRICT	qt_registros_w
	from   	regra_escala_partic_cir
	where	coalesce(ie_situacao,'A') = 'A';

	if (qt_registros_w > 0) then
		SELECT 	max(hr_inicio),
			max(hr_inicio) + (max(nr_minuto_duracao) /1440) - (1/86400),
			max(cd_agenda)
		into STRICT	hr_inicio_w,
			hr_fim_w,
			cd_agenda_w
		FROM   	agenda_paciente
		WHERE  	nr_sequencia = nr_seq_agenda_p
		and	ie_status_agenda not in ('C','L','B');

		if (hr_inicio_w IS NOT NULL AND hr_inicio_w::text <> '') then
			dia_da_Semana_w := Obter_Cod_Dia_Semana(hr_inicio_w);
			open C01;
			loop
			fetch C01 into
				cd_pessoa_fisica_w,
				nr_seq_equipe_w,
				cd_funcao_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin
				insert	into	profissional_agenda(
							nr_sequencia,
							cd_profissional,
							cd_funcao,
							nr_seq_agenda,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							ie_indicacao,
							nr_seq_equipe,
							cd_especialidade)
				values (nextval('profissional_agenda_seq'),
							cd_pessoa_fisica_w,
							cd_funcao_w,
							nr_seq_agenda_p,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							'N',
							nr_seq_equipe_w,
							null);
				commit;
				end;
			end loop;
			close C01;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
