CREATE OR REPLACE PROCEDURE gerar_escala_resumo_medico ( cd_medico_p bigint, dt_mes_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_entrada_w	 	timestamp;
dt_saida_w 		timestamp;
qt_hora_w		double precision;
vl_hora_w		double precision;
vl_total_medico_w	double precision;


BEGIN

select	dt_entrada,
	dt_saida,
	(qt_min_total / 60)
into STRICT	dt_entrada_w,
	dt_saida_w,
	qt_hora_w
from	medico_controle
where	nr_sequencia = (SELECT	max(nr_sequencia)
			from	medico_controle
			where	trunc(dt_referencia) = trunc(clock_timestamp()));


vl_hora_w	:= obter_valor_hora_medico_escala(cd_medico_p);

if (coalesce(vl_hora_w,0) = 0) then
	-- O usuário não possui o valor de hora cadastrada. Por favor verifique.
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264468);
end if;

vl_total_medico_w := (vl_hora_w * qt_hora_w);

insert into escala_resumo_medico(nr_sequencia, cd_estabelecimento, dt_atualizacao,
	 nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
	 cd_medico, dt_entrada, qt_hora,
	 dt_saida, vl_hora, vl_total_medico)
values (nextval('escala_resumo_medico_seq'), cd_estabelecimento_p, clock_timestamp(),
	 nm_usuario_p, clock_timestamp(), nm_usuario_p,
	 cd_medico_p, dt_entrada_w, qt_hora_w,
	 dt_saida_w, vl_hora_w, vl_total_medico_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
