SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_estabilidade_solucoes (nr_prescricao_p bigint) AS $body$
DECLARE


nr_prescr_w		bigint;
nr_seq_sol_w		integer;
qt_hor_estab_w	double precision;

c01 CURSOR FOR
SELECT	b.nr_prescricao,
	b.nr_seq_solucao
from	prescr_solucao b,
	prescr_medica a
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_prescricao	= b.nr_prescricao
order by
	nr_seq_solucao;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	open c01;
	loop
	fetch c01 into	nr_prescr_w,
			nr_seq_sol_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	min(qt_horas_estabilidade)
		into STRICT	qt_hor_estab_w
		from	prescr_material
		where	ie_agrupador		= 4
		and	nr_prescricao 	= nr_prescr_w
		and	nr_sequencia_solucao	= nr_seq_sol_w;

		update	prescr_solucao
		set	qt_horas_estabilidade 	= qt_hor_estab_w
		where	nr_prescricao			= nr_prescr_w
		and	nr_seq_solucao		= nr_seq_sol_w;

		end;
	end loop;
	close c01;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
