SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_etapa_disp_prod_proc ( nr_seq_etapa_p bigint, dt_horario_p timestamp) AS $body$
DECLARE


cd_material_w 			bigint;
qt_dose_util_w 			double precision;
qt_consumo_w			double precision;
cd_unidade_medida_w 	varchar(30);
nr_seq_lote_fornec_w 	bigint;
nr_seq_gedipa_cabine_w  bigint;
nr_seq_gedipa_hor_w		gedipa_etapa_hor.nr_sequencia%type;
nr_seq_ficha_tecnica_w	material.nr_seq_ficha_tecnica%type;

c01 CURSOR FOR
	SELECT	a.cd_material,
			a.qt_dose_util,
			a.cd_unidade_medida,
			a.nr_seq_lote_fornec,
			a.nr_seq_gedipa_cabine,
			b.nr_seq_ficha_tecnica
	from	w_gedipa_preparo a,
			material b
	where	a.cd_material = b.cd_material
	and		a.qt_dose_util > 0
    and     a.nm_usuario = wheb_usuario_pck.get_nm_usuario
	and 	a.cd_material in (	SELECT 	z.cd_material
								from 	gedipa_etapa_hor z
								where 	z.nr_seq_etapa_gedipa = nr_seq_etapa_p
								and 	dt_horario = dt_horario_p)
	
union

	select	a.cd_material,
			a.qt_dose_util,
			a.cd_unidade_medida,
			a.nr_seq_lote_fornec,
			a.nr_seq_gedipa_cabine,
			b.nr_seq_ficha_tecnica
	from	w_gedipa_preparo a,
			material b
	where	a.cd_material = b.cd_material
    and     a.qt_dose_util > 0
    and     a.nm_usuario = wheb_usuario_pck.get_nm_usuario
	and 	b.nr_seq_ficha_tecnica in (	select 	c.nr_seq_ficha_tecnica												
										from 	gedipa_etapa_hor z,
												material c
										where 	c.cd_material = z.cd_material
										and		z.nr_seq_etapa_gedipa = nr_seq_etapa_p
										and 	z.dt_horario = dt_horario_p);


BEGIN

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_dose_util_w,
	cd_unidade_medida_w,
	nr_seq_lote_fornec_w,
	nr_seq_gedipa_cabine_w,
	nr_seq_ficha_tecnica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_gedipa_hor_w
	from	gedipa_etapa_hor a,
			material b
	where	a.cd_material = b.cd_material
	and		a.nr_seq_etapa_gedipa = nr_seq_etapa_p
	and		((b.cd_material = cd_material_w) or (b.nr_seq_ficha_tecnica = nr_seq_ficha_tecnica_w))
	and 	dt_horario = dt_horario_p;

	insert 	into gedipa_etapa_prod_disp(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_gedipa_hor,
			cd_material,
			qt_material,
			cd_unidade_medida,
			nr_seq_lote_fornec,
			nr_seq_gedipa_cabine)
	values (nextval('gedipa_etapa_prod_disp_seq'),
			clock_timestamp(),
			wheb_usuario_pck.get_nm_usuario,
			clock_timestamp(),
			wheb_usuario_pck.get_nm_usuario,
			nr_seq_gedipa_hor_w,
			cd_material_w,
			qt_dose_util_w,
			cd_unidade_medida_w,
			nr_seq_lote_fornec_w,
			null);

	qt_consumo_w 	:= 	obter_conversao_unid_med_cons(cd_material_w, cd_unidade_medida_w, qt_dose_util_w);

	update 	gedipa_estoque_cabine
	set 	qt_estoque = qt_estoque - qt_consumo_w
	where 	nr_sequencia = nr_seq_gedipa_cabine_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
