CREATE OR REPLACE PROCEDURE gerar_etapa_disp_prod (nr_seq_etapa_hor_p bigint, nr_seq_gedipa_hor_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w 			bigint;
qt_dose_util_w 			double precision;
qt_consumo_w			double precision;
cd_unidade_medida_w 	varchar(30);
nr_seq_lote_fornec_w 	bigint;
nr_seq_gedipa_cabine_w  bigint;

c01 CURSOR FOR
	SELECT	cd_material,
			qt_dose_util,
			cd_unidade_medida,
			nr_seq_lote_fornec,
			nr_seq_gedipa_cabine
	from	w_gedipa_preparo
	where	nr_seq_etapa_hor = nr_seq_etapa_hor_p
	and		qt_dose_util > 0;


BEGIN

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_dose_util_w,
	cd_unidade_medida_w,
	nr_seq_lote_fornec_w,
	nr_seq_gedipa_cabine_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert 	into gedipa_etapa_prod_disp(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_gedipa_hor,
			cd_material,
			qt_material,
			cd_unidade_medida,
			nr_seq_lote_fornec,
			nr_seq_gedipa_cabine)
	values (nextval('gedipa_etapa_prod_disp_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_gedipa_hor_p,
			cd_material_w,
			qt_dose_util_w,
			cd_unidade_medida_w,
			nr_seq_lote_fornec_w,
			null);

	qt_consumo_w 	:= 	obter_conversao_unid_med_cons(cd_material_w, cd_unidade_medida_w, qt_dose_util_w);

	update 	gedipa_estoque_cabine
	set 	qt_estoque = qt_estoque - qt_consumo_w
	where 	nr_sequencia = nr_seq_gedipa_cabine_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
