CREATE OR REPLACE PROCEDURE gerar_etapa_lote_atendimento ( nr_lote_p bigint, ie_evento_p text, nm_usuario_p text ) AS $body$
DECLARE

 
nr_interno_conta_w	conta_paciente.nr_interno_conta%type;
	
C01 CURSOR FOR 
	SELECT	a.nr_interno_conta 
	FROM atendimento_paciente c, sus_laudo_paciente a
LEFT OUTER JOIN sus_lote_autor b ON (a.nr_seq_lote = b.nr_sequencia)
WHERE a.nr_seq_lote   = nr_lote_p and a.nr_atendimento = c.nr_atendimento group by a.nr_interno_conta;
	

BEGIN 
	 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		CALL gerar_etapa_conta(nr_interno_conta_w, ie_evento_p, nm_usuario_p);
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
