CREATE OR REPLACE PROCEDURE gerar_etapa_prod_disp (nm_usuario_p text, nr_seq_gedipa_hor_p bigint, cd_material_p bigint default 0) AS $body$
DECLARE


nr_sequencia_w			bigint;
cd_material_w 			bigint;
qt_dose_util_w 			double precision;
cd_unidade_medida_w 	varchar(30);
nr_seq_lote_fornec_w 	bigint;
nr_seq_gedipa_cabine_w 	bigint;

qt_consumo_w 			double precision;
nr_seq_ficha_tecnica_w	bigint;

c01 CURSOR FOR
	SELECT 	a.cd_material,
			a.qt_dose_util,
			a.cd_unidade_medida,
			a.nr_seq_lote_fornec,
			a.nr_seq_gedipa_cabine
	from 	w_gedipa_preparo a,
			material b
	where 	a.cd_material = b.cd_material
	and		a.nm_usuario = nm_usuario_p
	and		a.qt_dose_util > 0
	and	((a.cd_material = cd_material_p) or (b.nr_seq_ficha_tecnica = nr_seq_ficha_tecnica_w) or (cd_material_p = 0));

BEGIN

select	max(nr_seq_ficha_tecnica)
into STRICT	nr_seq_ficha_tecnica_w
from	material
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_dose_util_w,
	cd_unidade_medida_w,
	nr_seq_lote_fornec_w,
	nr_seq_gedipa_cabine_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select 	nextval('gedipa_etapa_prod_disp_seq')
	into STRICT	nr_sequencia_w
	;

	insert 	into gedipa_etapa_prod_disp(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_gedipa_hor,
			cd_material,
			qt_material,
			cd_unidade_medida,
			nr_seq_lote_fornec,
			nr_seq_gedipa_cabine)
		values (nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_gedipa_hor_p,
			cd_material_w,
			qt_dose_util_w,
			cd_unidade_medida_w,
			nr_seq_lote_fornec_w,
			null);

	qt_consumo_w 	:= 	obter_conversao_unid_med_cons(cd_material_w, cd_unidade_medida_w, qt_dose_util_w);

	update 	gedipa_estoque_cabine
	set 	qt_estoque = qt_estoque - qt_consumo_w
	where 	nr_sequencia = nr_seq_gedipa_cabine_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
