CREATE OR REPLACE PROCEDURE gerar_eventos_repasse (nr_repasse_terceiro_p bigint, ie_tipo_evento_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_evento_w		bigint;
nr_seq_terceiro_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
dt_referencia_w		timestamp;
nr_seq_regra_envio_sms_w	bigint;

c01 CURSOR FOR 
SELECT	a.cd_pessoa_fisica 
from	terceiro_pessoa_fisica a 
where	a.nr_seq_terceiro	= nr_seq_terceiro_w 
and	dt_referencia_w between a.dt_inicio_vigencia and a.dt_fim_vigencia;


BEGIN 
 
dt_referencia_w	:= clock_timestamp();
 
select	max(nr_seq_terceiro) 
into STRICT	nr_seq_terceiro_w 
from	repasse_terceiro 
where	nr_repasse_terceiro	= nr_repasse_terceiro_p;
 
if (nr_seq_terceiro_w IS NOT NULL AND nr_seq_terceiro_w::text <> '') then 
 
	select coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_regra_envio_sms_w 
	from 	regra_envio_sms	 
	where 	ie_evento_disp 		= coalesce(ie_tipo_evento_p,'RF') 
	and	cd_estabelecimento	= cd_estabelecimento_p 
	and	coalesce(ie_situacao,'A') = 'A';
 
	if (nr_seq_regra_envio_sms_w > 0) then 
 
		select 	coalesce(max(nr_seq_evento),0) 
		into STRICT	nr_seq_evento_w 
		from 	regra_envio_sms 
		where 	nr_sequencia		= nr_seq_regra_envio_sms_w 
		and	cd_estabelecimento	= cd_estabelecimento_p 
		and	coalesce(ie_situacao,'A') = 'A';
	end if;
 
	if (nr_seq_evento_w IS NOT NULL AND nr_seq_evento_w::text <> '') then 
		 
		CALL gerar_evento_paciente(nr_seq_evento_w, 
				null, 
				null, 
				null, 
				nm_usuario_p, 
				nr_repasse_terceiro_p);
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
