-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evento_agendamento_int ( nr_seq_evento_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_laudo_p bigint, nm_usuario_p text, cd_agenda_p bigint, hr_inicio_p timestamp, cd_medico_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, ds_observacao_p text, ie_trigger_p text default 'S') AS $body$
DECLARE


ie_forma_ev_w		varchar(15);
ie_pessoa_destino_w	varchar(15);
cd_pf_destino_w		varchar(10);
ds_mensagem_w		varchar(4000);
ds_titulo_w		varchar(100);
cd_pessoa_destino_w	varchar(10);
nr_sequencia_w		bigint;
ds_maquina_w		varchar(80);
nm_paciente_w		varchar(60);
ds_unidade_w		varchar(60);
ds_setor_atendimento_w	varchar(60);
ie_usuario_aceite_w	varchar(1);
qt_corresp_w		integer;
cd_setor_atendimento_w	integer;
cd_perfil_w		integer;
cd_pessoa_regra_w	varchar(10);
nr_ramal_w		varchar(10);
nr_telefone_w		varchar(10);
cd_convenio_w		bigint;

ds_Agenda_w		varchar(100);
nm_pac_Agenda_w		varchar(60);
hr_inicio_w		timestamp;
nm_medico_w		varchar(60);
ds_procedimento_w	varchar(255);
dt_cancelamento_w	timestamp;
nm_usuario_destino_w	varchar(15);
cd_setor_atend_pac_w	integer;
ds_cid_w		varchar(240);
cd_cid_w		varchar(10);
ds_ramal_usuario_w	varchar(255);

ie_tipo_internado_w	varchar(1);
nr_atendimento_w	bigint;
ds_usuario_w		varchar(40);
cd_setor_atend_w	integer;

C01 CURSOR FOR
	SELECT	ie_forma_ev,
		ie_pessoa_destino,
		cd_pf_destino,
		coalesce(ie_usuario_aceite,'N'),
		cd_setor_atendimento,
		cd_perfil
	from	ev_evento_regra_dest
	where	nr_seq_evento	= nr_seq_evento_p
	and	coalesce(cd_convenio, coalesce(cd_convenio_w,0))	= coalesce(cd_convenio_w,0)
	and	coalesce(cd_setor_atend_pac, coalesce(cd_setor_atend_pac_w,0))	= coalesce(cd_setor_atend_pac_w,0)
	order by ie_forma_ev;

C02 CURSOR FOR
	SELECT	obter_dados_usuario_opcao(nm_usuario,'C')
	from	usuario_setor_v
	where	cd_setor_atendimento = cd_setor_atendimento_w
	and	ie_forma_ev_w in (2,3)
	and	(obter_dados_usuario_opcao(nm_usuario,'C') IS NOT NULL AND (obter_dados_usuario_opcao(nm_usuario,'C'))::text <> '');

C03 CURSOR FOR
	SELECT	obter_dados_usuario_opcao(nm_usuario,'C'),
			nm_usuario
	from	usuario_perfil
	where	cd_perfil = cd_perfil_w
	and	ie_forma_ev_w in (2,3)
	and	(obter_dados_usuario_opcao(nm_usuario,'C') IS NOT NULL AND (obter_dados_usuario_opcao(nm_usuario,'C'))::text <> '')
	and (ie_pessoa_destino_w <> '16');
	
C04 CURSOR FOR
	SELECT	obter_dados_usuario_opcao(nm_usuario,'C')
	from	usuario_setor_v
	where	cd_setor_atendimento = cd_setor_atend_w
	and	ie_forma_ev_w in (2,3)
	and	(obter_dados_usuario_opcao(nm_usuario,'C') IS NOT NULL AND (obter_dados_usuario_opcao(nm_usuario,'C'))::text <> '')
	and (obter_se_perfil_usuario(cd_perfil_w,nm_usuario) = 'S'
	or (ie_pessoa_destino_w <> '16')
	or (coalesce(cd_perfil_w::text, '') = ''));
	

BEGIN
select	substr(obter_inf_sessao(0),1,80)
into STRICT	ds_maquina_w
;

select	coalesce(max(ie_pac_inter_legenda),'N')
into STRICT	ie_tipo_internado_w
from	parametro_agenda
where	cd_estabelecimento =  wheb_usuario_pck.get_cd_estabelecimento;

if (ie_tipo_internado_w = 'N') then
	select	max(b.nr_atendimento)
	into STRICT	nr_atendimento_w
	from    setor_atendimento c,
		unidade_atendimento a,
		atendimento_paciente b 
	where   a.nr_atendimento        = b.nr_atendimento 
	and     a.cd_setor_atendimento  = c.cd_setor_atendimento
	and     c.cd_classif_setor      in (3,4,8)
	and	b.cd_pessoa_fisica      = cd_pessoa_fisica_p;
else
	select	max(b.nr_atendimento)
	into STRICT	nr_atendimento_w
	from    atendimento_paciente b
	where   b.ie_tipo_atendimento	= 1
	and	b.cd_pessoa_fisica      = cd_pessoa_fisica_p
	and	coalesce(b.dt_alta::text, '') = '';
end if;


select	coalesce(max(obter_convenio_atendimento(nr_atendimento_w)), 0),
	coalesce(max(obter_setor_atendimento(nr_atendimento_w)),0)
into STRICT	cd_convenio_w,
	cd_setor_atend_w
;


select	ds_titulo,
	ds_mensagem
into STRICT	ds_titulo_w,
	ds_mensagem_w
from	ev_evento
where	nr_sequencia	= nr_seq_evento_p;

select	substr(obter_nome_pf(cd_pessoa_fisica_p),1,60),
	substr(obter_unidade_atendimento(nr_atendimento_w,'A','U'),1,60),
	substr(obter_unidade_atendimento(nr_atendimento_w,'A','RA'),1,60),
	substr(obter_unidade_atendimento(nr_atendimento_w,'A','TL'),1,60),
	substr(obter_unidade_atendimento(nr_atendimento_w,'A','S'),1,60),
	substr(obter_unidade_atendimento(nr_atendimento_w,'A','CS'),1,60),
	substr(obter_nome_pf(cd_medico_p),1,60)
into STRICT	nm_paciente_w,
	ds_unidade_w,
	nr_ramal_w,
	nr_telefone_w,
	ds_setor_atendimento_w,
	cd_setor_atend_pac_w,
	nm_medico_w
;

select	max(ds_agenda)
into STRICT	ds_agenda_w
from	agenda
where	cd_agenda	= cd_Agenda_p;


select	max(substr(ds_procedimento,1,255))
into STRICT	ds_procedimento_w
from	procedimento
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

select	max(substr(obter_cid_atendimento(nr_atendimento_w, 'P'),1,10)),
	max(substr(obter_desc_cid_doenca(obter_cid_atendimento(nr_atendimento_w, 'P')),1,240)),
	max(substr(obter_dados_usuario_opcao(nm_usuario_p,'M'),1,255))
into STRICT	cd_cid_w,
	ds_cid_w,
	ds_ramal_usuario_w
;

select	max(substr(ds_usuario,1,40))
into STRICT	ds_usuario_w
from	usuario
where	nm_usuario = nm_usuario_p;

ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@paciente',nm_paciente_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@quarto',ds_unidade_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@setor',ds_setor_atendimento_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@atendimento',nr_atendimento_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@ramal',nr_ramal_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@telefone',nr_telefone_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@usuarioramal',ds_ramal_usuario_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@obsagendamento',ds_observacao_p),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@nomeusuario',ds_usuario_w),1,4000);


ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@agenda',ds_agenda_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@dtinicioagenda',PKG_DATE_FORMATERS_TZ.TO_VARCHAR(hr_inicio_p, 'timestamp', ESTABLISHMENT_TIMEZONE_UTILS.getTimeZone)),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@medicoagenda',nm_medico_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@procagenda',ds_procedimento_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@cd_cid',cd_cid_w),1,4000);
ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w,'@ds_cid',ds_cid_w),1,4000);


select	nextval('ev_evento_paciente_seq')
into STRICT	nr_sequencia_w
;

insert into ev_evento_paciente(
	nr_sequencia,
	nr_seq_evento,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_pessoa_fisica,
	nr_atendimento,
	ds_titulo,
	ds_mensagem,
	ie_status,
	ds_maquina,
	dt_evento,
	dt_liberacao)
values (	nr_sequencia_w,
	nr_seq_evento_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_pessoa_fisica_p,
	nr_atendimento_w,
	ds_titulo_w,
	ds_mensagem_w,
	'G',
	ds_maquina_w,
	clock_timestamp(),
	clock_timestamp());

open C01;
loop
fetch C01 into
	ie_forma_ev_w,
	ie_pessoa_destino_w,
	cd_pf_destino_w,
	ie_usuario_aceite_w,
	cd_setor_atendimento_w,
	cd_perfil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_pessoa_destino_w	:= null;
	qt_corresp_w	:= 1;
	if (ie_pessoa_destino_w = '1') then /* Medico do atendimento */
		begin
		select	max(cd_medico_atendimento)
		into STRICT	cd_pessoa_destino_w
		from	atendimento_paciente
		where	nr_atendimento	= nr_atendimento_w;
		end;
	elsif (ie_pessoa_destino_w = '2') then /*Medico responsavel pelo paciente*/
		begin
		select	max(cd_medico_resp)
		into STRICT	cd_pessoa_destino_w
		from	atendimento_paciente
		where	nr_atendimento	= nr_atendimento_w;
		end;
	elsif (ie_pessoa_destino_w = '3') then /*Medico laudante*/
		begin
		select	max(cd_medico_resp)
		into STRICT	cd_pessoa_destino_w
		from	laudo_paciente
		where	nr_sequencia	= nr_seq_laudo_p;
		end;
	elsif (ie_pessoa_destino_w = '4') then /*Medico referido*/
		begin
		select	max(cd_medico_referido)
		into STRICT	cd_pessoa_destino_w
		from	atendimento_paciente
		where	nr_atendimento	= nr_atendimento_w;
		end;
	elsif (ie_pessoa_destino_w = '5') or (ie_pessoa_destino_w = '12') then /*Pessoa fixa ou Usuario fixo*/
		cd_pessoa_destino_w	:= cd_pf_destino_w;
	elsif (ie_pessoa_destino_w = '13') or (ie_pessoa_destino_w = '16') then /* Usuario do setor_atendimento */
		open C04;
		loop
		fetch C04 into
			cd_pessoa_regra_w;
		EXIT WHEN NOT FOUND; /* apply on C04 */
			begin
			if (cd_pessoa_regra_w IS NOT NULL AND cd_pessoa_regra_w::text <> '') then
			
				insert into ev_evento_pac_destino(
					nr_sequencia,
					nr_seq_ev_pac,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_pessoa_fisica,
					ie_forma_ev,
					ie_status,
					dt_ciencia,
					ie_pessoa_destino,
					dt_evento)
				values (	nextval('ev_evento_pac_destino_seq'),
					nr_sequencia_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_pessoa_regra_w,
					ie_forma_ev_w,
					'G',
					null,
					ie_pessoa_destino_w,
					clock_timestamp());
			end if;
			end;
		end loop;
		close C04;
	end if;

	if (ie_usuario_aceite_w = 'S') and (cd_pessoa_destino_w IS NOT NULL AND cd_pessoa_destino_w::text <> '') and (ie_forma_ev_w = '1') then
		begin
		select	count(*)
		into STRICT	qt_corresp_w
		from	pessoa_fisica_corresp
		where	cd_pessoa_fisica	= cd_pessoa_destino_w
		and	ie_tipo_corresp		= 'MCel'
		and	ie_tipo_doc		= 'AE';
		end;
	elsif (ie_usuario_aceite_w = 'S') and (cd_pessoa_destino_w IS NOT NULL AND cd_pessoa_destino_w::text <> '') and (ie_forma_ev_w = '3') then
		begin
		select	count(*)
		into STRICT	qt_corresp_w
		from	pessoa_fisica_corresp
		where	cd_pessoa_fisica	= cd_pessoa_destino_w
		and	ie_tipo_corresp		= 'CI'
		and	ie_tipo_doc		= 'AE';
		end;
	elsif (ie_usuario_aceite_w = 'S') and (cd_pessoa_destino_w IS NOT NULL AND cd_pessoa_destino_w::text <> '') and (ie_forma_ev_w = '4') then
		begin
		select	count(*)
		into STRICT	qt_corresp_w
		from	pessoa_fisica_corresp
		where	cd_pessoa_fisica	= cd_pessoa_destino_w
		and	ie_tipo_corresp		= 'Email'
		and	ie_tipo_doc		= 'AE';
		end;
	end if;

	if (cd_pessoa_destino_w IS NOT NULL AND cd_pessoa_destino_w::text <> '') and (qt_corresp_w > 0) then
		begin
		insert into ev_evento_pac_destino(
			nr_sequencia,
			nr_seq_ev_pac,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			ie_forma_ev,
			ie_status,
			dt_ciencia,
			ie_pessoa_destino,
			dt_evento)
		values (	nextval('ev_evento_pac_destino_seq'),
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_destino_w,
			ie_forma_ev_w,
			'G',
			null,
			ie_pessoa_destino_w,
			clock_timestamp());
		end;
	end if;

	open C02;
	loop
	fetch C02 into
		cd_pessoa_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (cd_pessoa_regra_w IS NOT NULL AND cd_pessoa_regra_w::text <> '') then
		
			insert into ev_evento_pac_destino(
				nr_sequencia,
				nr_seq_ev_pac,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_pessoa_fisica,
				ie_forma_ev,
				ie_status,
				dt_ciencia,
				ie_pessoa_destino,
				dt_evento)
			values (	nextval('ev_evento_pac_destino_seq'),
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_regra_w,
				ie_forma_ev_w,
				'G',
				null,
				ie_pessoa_destino_w,
				clock_timestamp());
		end if;
		end;
	end loop;
	close C02;

	open C03;
	loop
	fetch C03 into
		cd_pessoa_regra_w,
		nm_usuario_destino_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin
		if (cd_pessoa_regra_w IS NOT NULL AND cd_pessoa_regra_w::text <> '') then
			insert into ev_evento_pac_destino(
				nr_sequencia,
				nr_seq_ev_pac,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_pessoa_fisica,
				ie_forma_ev,
				ie_status,
				dt_ciencia,
				nm_usuario_Dest,
				ie_pessoa_destino,
				dt_evento)
			values (	nextval('ev_evento_pac_destino_seq'),
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_regra_w,
				ie_forma_ev_w,
				'G',
				null,
				nm_usuario_destino_w,
				ie_pessoa_destino_w,
				clock_timestamp());
		end if;
		end;
	end loop;
	close C03;

	end;
end loop;
close C01;

if (ie_trigger_p = 'N') then
	commit;
end if;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_evento_agendamento_int ( nr_seq_evento_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_laudo_p bigint, nm_usuario_p text, cd_agenda_p bigint, hr_inicio_p timestamp, cd_medico_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, ds_observacao_p text, ie_trigger_p text default 'S') FROM PUBLIC;

