CREATE OR REPLACE PROCEDURE gerar_evento_alt_medico_resp_atx ( nr_atendimento_p bigint, nm_usuario_p text, cd_medico_resp_p text default null) AS $body$
DECLARE

 
nr_seq_evento_w		bigint;
cd_estabelecimento_w	smallint;
cd_pessoa_fisica_w	varchar(10);
qt_idade_w			bigint;
ie_carater_inter_sus_w		varchar(10);
ie_tipo_atendimento_w		varchar(10);
nr_seq_classificacao_w		bigint;
ie_clinica_w			integer;

C01 CURSOR FOR 
	SELECT	a.nr_seq_evento 
	FROM regra_envio_sms a
LEFT OUTER JOIN regra_envio_sms_atend b ON (a.nr_sequencia = b.nr_seq_regra)
WHERE a.cd_estabelecimento	= cd_estabelecimento_w and a.ie_evento_disp		= 'AMR' and coalesce(b.ie_carater_inter_sus,coalesce(ie_carater_inter_sus_w,0)) = coalesce(ie_carater_inter_sus_w,'0') and coalesce(b.ie_tipo_atendimento,coalesce(ie_tipo_atendimento_w,0)) = coalesce(ie_tipo_atendimento_w,'0') and coalesce(b.nr_seq_classificacao,coalesce(nr_seq_classificacao_w,0)) = coalesce(nr_seq_classificacao_w,'0') and coalesce(b.ie_clinica,coalesce(ie_clinica_w,0)) = coalesce(ie_clinica_w,'0') and qt_idade_w between coalesce(a.qt_idade_min,0) and coalesce(a.qt_idade_max,9999) and coalesce(a.ie_situacao,'A') = 'A' and (coalesce(a.cd_setor_atendimento,0) = coalesce(obter_setor_atendimento(nr_atendimento_p),0) or coalesce(a.cd_setor_atendimento::text, '') = '') and (coalesce(a.ie_tipo_guia,0) = coalesce(obter_tipo_guia_convenio(nr_atendimento_p),0) or coalesce(a.ie_tipo_guia::text, '') = '' );
BEGIN
 
select	cd_pessoa_fisica, 
	cd_estabelecimento, 
	ie_carater_inter_sus, 
	ie_tipo_atendimento, 
	nr_seq_classificacao, 
	ie_clinica 
into STRICT	cd_pessoa_fisica_w, 
	cd_estabelecimento_w, 
	ie_carater_inter_sus_w, 
	ie_tipo_atendimento_w, 
	nr_seq_classificacao_w, 
	ie_clinica_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_w,clock_timestamp(),'A'),0);
open C01;
loop 
fetch C01 into	 
	nr_seq_evento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_w,null,nm_usuario_p,null,null,null,NULL,cd_medico_resp_p);
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
