SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evento_atend_pa (nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
qt_atend_tipo_guia_hc_w		bigint;
nr_seq_evento_w			bigint;
ie_tipo_atendimento_w		smallint;

C01 CURSOR FOR 
	SELECT	nr_seq_evento 
	from	regra_envio_sms 
	where	cd_estabelecimento	= cd_estabelecimento_p 
	and	ie_evento_disp 		= 'APHC' 
	and	coalesce(ie_situacao,'A') = 'A';
			

BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
 
	select	max(ie_tipo_atendimento) 
	into STRICT	ie_tipo_atendimento_w 
	from	atendimento_Paciente 
	where	nr_atendimento	= nr_atendimento_p;
 
	select	count(*) 
	into STRICT	qt_atend_tipo_guia_hc_w 
	from	atendimento_paciente a, 
		atend_categoria_convenio b 
	where  a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	a.nr_atendimento = b.nr_atendimento	 
	and	coalesce(a.dt_alta::text, '') = '' 
	and   b.ie_tipo_guia = 'H';
	 
	if (qt_atend_tipo_guia_hc_w >= 1) and (ie_tipo_atendimento_w = 3) then 
		 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_evento_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
			CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,null,nm_usuario_p,null);	
			end;
		end loop;
		close C01;
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
