CREATE OR REPLACE PROCEDURE gerar_evento_classif_atend ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

nr_seq_evento_w		bigint;
qt_reg_w		bigint;
qt_idade_w		bigint;
nr_seq_classif_w	bigint;
		
 
C01 CURSOR FOR 
	SELECT	nr_seq_evento 
	from	regra_envio_sms 
	where	cd_estabelecimento	= cd_estabelecimento_p 
	and	ie_evento_disp = 'C' 
	and	qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999) 
	and	(((nr_seq_classif IS NOT NULL AND nr_seq_classif::text <> '') and (coalesce(nr_seq_classif, nr_seq_classif_w) = nr_seq_classif_w)) or 
		 ((coalesce(nr_seq_classif::text, '') = '') and (obter_classif_regra_pf(nr_sequencia,coalesce(nr_seq_classif_w,0)) = 'S'))) 
	and	coalesce(ie_situacao,'A') = 'A';


BEGIN 
 
select	count(*) 
into STRICT	qt_reg_w 
from	pessoa_classif a, 
	atendimento_paciente b 
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
and	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
and	coalesce(dt_inicio_vigencia,b.dt_entrada) <= b.dt_entrada 
and	coalesce(dt_final_vigencia,b.dt_entrada) >= b.dt_entrada 
and	nr_atendimento = nr_atendimento_p;
 
qt_idade_w	 := coalesce(obter_idade_pf(cd_pessoa_fisica_p,clock_timestamp(),'A'),0);
nr_seq_classif_w := obter_classificacao_pf(cd_pessoa_fisica_p);
 
if (qt_reg_w >= 1) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,null,nm_usuario_p,null);
		end;
	end loop;
	close C01;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
