CREATE OR REPLACE PROCEDURE gerar_evento_liber_leito (nr_seq_unidade_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	a.nr_seq_evento 
	from	regra_envio_sms a 
	where	a.cd_estabelecimento	= cd_estabelecimento_p 
	and	a.ie_evento_disp	= 'SLAL' 
	and	coalesce(ie_situacao,'A') = 'A';

qt_aguardando_limp_w		bigint;
nr_seq_evento_aguard_limp_w 	bigint;
cd_setor_atendimento_w		integer;
	

BEGIN 
 
select	count(*) 
	into STRICT 	qt_aguardando_limp_w 
from  	sl_unid_atend 
where 	nr_seq_unidade = nr_seq_unidade_p 
and  	trunc(dt_prevista) = trunc(clock_timestamp()) 
and  	ie_status_serv = 'P' 
and	ie_evento = 'LL';
 
select	cd_setor_atendimento 
into STRICT	cd_setor_atendimento_w 
from	unidade_atendimento 
where	nr_seq_interno = nr_seq_unidade_p;
 
	 
if (qt_aguardando_limp_w > 0) then 
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_evento_aguard_limp_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL gerar_evento_coord_escala(nr_seq_evento_aguard_limp_w,null,null,null,nm_usuario_p,nr_seq_unidade_p,cd_setor_atendimento_w,null, null,null,null);
		end;	
	end loop;
	close C01;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
