CREATE OR REPLACE PROCEDURE gerar_evento_lib_avaliacao ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, nr_seq_tipo_avaliacao_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE

nr_seq_evento_w bigint;
qt_reg_w bigint;

C01 CURSOR FOR 
 SELECT nr_seq_evento 
 FROM regra_envio_sms 
 WHERE cd_estabelecimento = cd_estabelecimento_p 
 AND ie_evento_disp = 'LA' 
 AND	coalesce(nr_seq_tipo_avaliacao,nr_seq_tipo_avaliacao_p) = nr_seq_tipo_avaliacao_p 
 and	coalesce(ie_situacao,'A') = 'A';


BEGIN 
 
 OPEN C01;
 LOOP 
 FETCH C01 INTO 
  nr_seq_evento_w;
 EXIT WHEN NOT FOUND; /* apply on C01 */
  BEGIN 
  CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,NULL,nm_usuario_p,NULL);
  END;
 END LOOP;
 CLOSE C01;
 
 
END;
