SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_evento_painel_cirurgia ( nr_cirurgia_p bigint, ie_evento_painel_p text, ie_inclusao_exclusao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_inclusao_exclusao_p

I - Inclusao
E - Exclusao
*/
nr_sequencia_w		bigint;
cd_profissional_w	varchar(10);
nr_sequencia_ww		bigint;
cd_estabelecimento_w		bigint := wheb_usuario_pck.get_cd_estabelecimento;
ie_gera_lib_w varchar(1) := 'N';
dt_lib_w timestamp;
ie_verif_lib_perfil_w varchar(1) := 'N';
ie_verif_lib_perfil_ww varchar(1) := 'S';

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	evento_cirurgia a
	where	a.ie_evento_painel	=	ie_evento_painel_p
	and	ie_inclusao_exclusao_p	=	'I'
	and	coalesce(a.ie_situacao,'A')  = 'A'
	and	((a.cd_estabelecimento = cd_estabelecimento_w) or (coalesce(a.cd_estabelecimento::text, '') = ''))
	and	not exists (	SELECT	1
				from	evento_cirurgia_paciente x
				where	x.nr_cirurgia = nr_cirurgia_p
				and	x.nr_seq_evento = a.nr_sequencia
				and	coalesce(x.ie_situacao,'A') = 'A')
	order by nr_seq_execucao;

BEGIN

select	obter_pessoa_fisica_usuario(nm_usuario_p,'C')
into STRICT	cd_profissional_w
;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ie_verif_lib_perfil_ww := 'S';

	select max(ie_verif_lib_perfil)
	into STRICT ie_verif_lib_perfil_w
	from evento_cirurgia e
	where e.nr_sequencia = nr_sequencia_w;

	if (coalesce(ie_verif_lib_perfil_w, 'N') = 'S') then
		select coalesce(max('S'), 'N')
		into STRICT ie_verif_lib_perfil_ww
		from evento_cirurgia a,
				evento_cirurgia_lib b
		where b.nr_seq_evento = a.nr_sequencia
		and a.nr_sequencia = nr_sequencia_w
		and b.cd_perfil = wheb_usuario_pck.get_cd_perfil
		and (b.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento or coalesce(b.cd_estabelecimento::text, '') = '');
	end if;

	if (ie_verif_lib_perfil_ww = 'S') then
		select max(ie_gera_lib_ev_painel)
		into STRICT ie_gera_lib_w
		from evento_cirurgia e
		where e.nr_sequencia = nr_sequencia_w;

		select	nextval('evento_cirurgia_paciente_seq')
		into STRICT 	nr_sequencia_ww
		;

		insert into evento_cirurgia_paciente(
			nr_sequencia,
			nr_seq_evento,
			nr_cirurgia,
			ds_observacao,
			dt_registro,
			cd_profissional,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_final_evento,
			ie_situacao,
			dt_liberacao)
		SELECT 	nr_sequencia_ww,
			nr_sequencia_w,
			nr_cirurgia_p,
			null,
			clock_timestamp(),
			cd_profissional_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			null,
			'A',
			CASE WHEN ie_gera_lib_w='S' THEN  clock_timestamp()  ELSE null END
		;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_evento_painel_cirurgia ( nr_cirurgia_p bigint, ie_evento_painel_p text, ie_inclusao_exclusao_p text, nm_usuario_p text) FROM PUBLIC;

