CREATE OR REPLACE PROCEDURE gerar_evento_parametro ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, nr_seq_procedimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_existe_evento_p INOUT text, cd_tipo_procedimento_p INOUT bigint, nr_seq_interno_p INOUT bigint, nr_acesso_dicom_p INOUT text, ds_diretorio_pacs_p INOUT text) AS $body$
DECLARE



ie_existe_evento_w	varchar(2);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_tipo_procedimento_w	smallint;
nr_seq_interno_w	bigint;
nr_acesso_dicom_w	varchar(20);
ds_diretorio_pacs_w	varchar(255);


BEGIN

select	max(obter_se_existe_evento(19))
into STRICT	ie_existe_evento_w
;

if (ie_existe_evento_w = 'S') then

	select	max(cd_procedimento),
		max(ie_origem_proced)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_prescricao_p;

	select	max(cd_tipo_procedimento)
	into STRICT	cd_tipo_procedimento_w
	from	procedimento
	where	cd_procedimento = cd_procedimento_w
	and	ie_origem_proced = ie_origem_proced_w;

	select	max(obter_seq_interno_prescr(nr_prescricao_p, nr_seq_prescricao_p, nr_seq_procedimento_p)),
		max(substr(obter_nr_acesso_dicom(nr_prescricao_p, nr_seq_prescricao_p, nr_seq_procedimento_p),1,20))
	into STRICT	nr_seq_interno_w,
		nr_acesso_dicom_w
	;

	select	max(ds_diretorio_pacs)
	into STRICT	ds_diretorio_pacs_w
	from	parametro_medico
	where	cd_estabelecimento = cd_estabelecimento_p;

end if;

ie_existe_evento_p	:= ie_existe_evento_w;
cd_tipo_procedimento_p	:= cd_tipo_procedimento_w;
nr_seq_interno_p	:= nr_seq_interno_w;
nr_acesso_dicom_p	:= nr_acesso_dicom_w;
ds_diretorio_pacs_p	:= ds_diretorio_pacs_w;

end;
